import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { FirebaseProvider, AuthProvider } from "@/context";
import { ProtectedRouteWrapper } from "@/components/auth";
import { MobileDebugConsole } from "@/components/MobileDebugConsole";
import Index from "./pages/Index";
import FirstStep from "./pages/FirstStep";
import SecondStep from "./pages/SecondStep";
import ResultsEnhanced from "./pages/ResultsEnhanced";
import Science from "./pages/Science";
import Login from "./pages/Login";
import Register from "./pages/Register";
import PasswordReset from "./pages/PasswordReset";
import Profile from "./pages/ProfileWithHistory";
import Dashboard from "./pages/Dashboard";

const queryClient = new QueryClient();

// No error suppression needed with Firebase-first architecture

const App: React.FC = () => (
  <QueryClientProvider client={queryClient}>
    <FirebaseProvider>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <Sonner />
          <BrowserRouter>
            <MobileDebugConsole />
            <Routes>
              {/* Public routes */}
              <Route path="/" element={<Index />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/reset-password" element={<PasswordReset />} />
              <Route path="/assessment" element={<FirstStep />} />
              <Route path="/second-step" element={<SecondStep />} />
              <Route path="/results" element={<ResultsEnhanced />} />
              <Route path="/science" element={<Science />} />

              {/* Protected routes */}
              <Route element={<ProtectedRouteWrapper />}>
                <Route path="/dashboard" element={<Dashboard />} />
                <Route path="/profile" element={<Profile />} />
              </Route>
            </Routes>
          </BrowserRouter>
        </TooltipProvider>
      </AuthProvider>
    </FirebaseProvider>
  </QueryClientProvider>
);

export default App;

// Firebase Services Index
// This file exports all Firebase services for easy importing

// Export Firebase app and services from config
export { app, auth, firestore, storage } from './config';

// Export authentication functions
export * from './auth';

// Export Firestore functions
export * from './firestore';

// Export Storage functions
export * from './storage';

/** @type {import('ts-jest').JestConfigWithTsJest} */
module.exports = {
  preset: 'ts-jest',
  testEnvironment: 'jsdom',
  moduleNameMapper: {
    '^@/(.*)$': '<rootDir>/src/$1',
    '\\.(css|less|scss|sass)$': '<rootDir>/src/components/results/JobValueCompass/__mocks__/styleMock.js'
  },
  testMatch: ['**/__tests__/**/*.test.(ts|tsx)'],
  transform: {
    '^.+\\.(ts|tsx)$': ['ts-jest', {
      tsconfig: 'tsconfig.test.json'
    }]
  },
  setupFilesAfterEnv: ['<rootDir>/jest.setup.js'],
  moduleFileExtensions: ['ts', 'tsx', 'js', 'jsx', 'json', 'node']
};

