// Firebase Integration Test
// This test validates Firebase integration within the React app context

import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import { render, screen, cleanup } from '@testing-library/react';
import { MemoryRouter } from 'react-router-dom';
import { AuthProvider } from '@/context/AuthContext';
import { FirebaseProvider } from '@/context/FirebaseContext';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { app, auth, firestore, storage } from '@/firebase/config';
import { getFirebaseConfig } from '@/utils/environment';

// Test wrapper component that doesn't include Router (to avoid conflicts)
const TestWrapper = ({ children }: { children: React.ReactNode }) => {
  const queryClient = new QueryClient({
    defaultOptions: {
      queries: { retry: false },
      mutations: { retry: false },
    },
  });

  return (
    <MemoryRouter>
      <QueryClientProvider client={queryClient}>
        <FirebaseProvider>
          <AuthProvider>
            {children}
          </AuthProvider>
        </FirebaseProvider>
      </QueryClientProvider>
    </MemoryRouter>
  );
};

// Simple test component to verify Firebase context
const TestComponent = () => {
  return (
    <div>
      <h1>Job Satisfaction Assessment</h1>
      <div data-testid="firebase-test">Firebase Integration Test</div>
      <div data-testid="auth-test">Auth Context Available</div>
    </div>
  );
};

describe('Firebase Integration Tests', () => {
  let queryClient: QueryClient;

  beforeEach(() => {
    queryClient = new QueryClient({
      defaultOptions: {
        queries: { retry: false },
        mutations: { retry: false },
      },
    });
  });

  afterEach(() => {
    cleanup();
    queryClient.clear();
  });

  describe('Firebase Services in App Context', () => {
    it('should initialize Firebase services without errors', () => {
      expect(app).toBeDefined();
      expect(auth).toBeDefined();
      expect(firestore).toBeDefined();
      expect(storage).toBeDefined();
    });

    it('should render app components with Firebase context', () => {
      render(
        <TestWrapper>
          <TestComponent />
        </TestWrapper>
      );

      expect(screen.getByText(/Job Satisfaction/i)).toBeInTheDocument();
      expect(screen.getByTestId('firebase-test')).toBeInTheDocument();
    });
  });

  describe('Auth Context Integration', () => {
    it('should provide auth context without errors', () => {
      render(
        <TestWrapper>
          <TestComponent />
        </TestWrapper>
      );

      expect(screen.getByTestId('auth-test')).toBeInTheDocument();
    });

    it('should handle Firebase emulator configuration', () => {
      const useEmulator = process.env.VITE_USE_FIREBASE_EMULATOR;

      if (useEmulator === 'true') {
        console.log('✅ Using Firebase emulator for testing');
      } else {
        console.log('📡 Using production Firebase services');
      }

      // Test should pass regardless of emulator setting
      expect(true).toBe(true);
    });
  });

  describe('Firebase Configuration Validation', () => {
    it('should have valid Firebase configuration in the app', () => {
      const config = getFirebaseConfig();

      expect(config.apiKey).toBeDefined();
      expect(config.projectId).toBeDefined();
      expect(config.authDomain).toBeDefined();
      expect(config.storageBucket).toBeDefined();
      expect(config.messagingSenderId).toBeDefined();
      expect(config.appId).toBeDefined();
    });

    it('should have consistent project configuration', () => {
      const config = getFirebaseConfig();
      const projectId = config.projectId;
      const authDomain = config.authDomain;
      const storageBucket = config.storageBucket;

      expect(authDomain).toContain(projectId);
      expect(storageBucket).toContain(projectId);
    });
  });

  describe('Error Handling', () => {
    it('should handle Firebase initialization errors gracefully', () => {
      // This test ensures that even if Firebase has issues, the app doesn't crash
      expect(() => {
        render(
          <TestWrapper>
            <TestComponent />
          </TestWrapper>
        );
      }).not.toThrow();
    });
  });

  describe('Environment Configuration', () => {
    it('should have proper environment variables set', () => {
      const config = getFirebaseConfig();

      // Validate environment variables are properly loaded
      expect(config.apiKey).not.toBe('');
      expect(config.projectId).not.toBe('your-project-id');
      expect(config.authDomain).not.toBe('your-auth-domain');
    });

    it('should have consistent project configuration', () => {
      const config = getFirebaseConfig();

      // Ensure we're using the correct project
      expect(config.projectId).toBe('jobjoy-e14e5');
      expect(config.authDomain).toBe('jobjoy-e14e5.firebaseapp.com');
      expect(config.storageBucket).toBe('jobjoy-e14e5.firebasestorage.app');
    });
  });
});

