import { getPerformance, trace, PerformanceTrace } from 'firebase/performance';
import { getAnalytics, logEvent } from 'firebase/analytics';
import { app } from '../firebase/config';

// Only initialize Analytics and Performance in production to avoid API key errors in development
const isUsingEmulator = import.meta.env.VITE_USE_FIREBASE_EMULATOR === 'true';
const isDevelopment = import.meta.env.MODE === 'development';

// Initialize Firebase Performance Monitoring (only in production)
const performance = !isUsingEmulator && !isDevelopment ? getPerformance(app) : null;

// Initialize Firebase Analytics (only in production)  
const analytics = !isUsingEmulator && !isDevelopment ? getAnalytics(app) : null;

/**
 * Performance monitoring utilities
 */
export const performanceMonitoring = {
  /**
   * Start a custom trace for performance monitoring
   * @param traceName - Name of the trace
   * @returns PerformanceTrace object or null if performance monitoring is disabled
   */
  startTrace: (traceName: string): PerformanceTrace | null => {
    if (!performance) {
      console.debug(`Performance trace '${traceName}' skipped in development mode`);
      return null;
    }
    return trace(performance, traceName);
  },

  /**
   * Record a performance metric within a trace
   * @param activeTrace - The active trace
   * @param metricName - Name of the metric
   * @param value - Value to record
   */
  recordMetric: (activeTrace: PerformanceTrace | null, metricName: string, value: number): void => {
    if (!activeTrace) {
      console.debug(`Performance metric '${metricName}' skipped in development mode`);
      return;
    }
    activeTrace.putMetric(metricName, value);
  },

  /**
   * Stop a trace and record its data
   * @param activeTrace - The active trace to stop
   */
  stopTrace: (activeTrace: PerformanceTrace | null): void => {
    if (!activeTrace) {
      console.debug('Performance trace stop skipped in development mode');
      return;
    }
    activeTrace.stop();
  },

  /**
   * Measure the execution time of a function
   * @param traceName - Name of the trace
   * @param fn - Function to measure
   * @returns The result of the function
   */
  measureExecution: async <T>(traceName: string, fn: () => Promise<T>): Promise<T> => {
    if (!performance) {
      console.debug(`Performance measurement '${traceName}' skipped in development mode`);
      return await fn();
    }

    const activeTrace = trace(performance, traceName);
    try {
      activeTrace.start();
      const result = await fn();
      return result;
    } finally {
      activeTrace.stop();
    }
  }
};

/**
 * Analytics event tracking utilities
 */
export const analyticsTracking = {
  /**
   * Log a custom event to Firebase Analytics
   * @param eventName - Name of the event
   * @param eventParams - Optional parameters for the event
   */
  logCustomEvent: (eventName: string, eventParams?: Record<string, any>): void => {
    logEvent(analytics, eventName, eventParams);
  },

  /**
   * Log a page view event
   * @param pageName - Name of the page
   * @param pageParams - Optional parameters for the page view
   */
  logPageView: (pageName: string, pageParams?: Record<string, any>): void => {
    logEvent(analytics, 'page_view', {
      page_title: pageName,
      page_location: window.location.href,
      page_path: window.location.pathname,
      ...pageParams
    });
  },

  /**
   * Log a user action event
   * @param actionName - Name of the action
   * @param actionParams - Optional parameters for the action
   */
  logUserAction: (actionName: string, actionParams?: Record<string, any>): void => {
    logEvent(analytics, 'user_action', {
      action_name: actionName,
      ...actionParams
    });
  },

  /**
   * Log an error event
   * @param errorCode - Error code or name
   * @param errorMessage - Error message
   * @param errorContext - Additional context about where the error occurred
   */
  logError: (errorCode: string, errorMessage: string, errorContext?: string): void => {
    logEvent(analytics, 'app_error', {
      error_code: errorCode,
      error_message: errorMessage,
      error_context: errorContext || 'unknown'
    });
  }
};

/**
 * Error reporting utilities
 */
export const errorReporting = {
  /**
   * Report an error to the console and analytics
   * @param error - The error object
   * @param context - Additional context about where the error occurred
   */
  reportError: (error: Error, context?: string): void => {
    console.error(`Error in ${context || 'unknown context'}:`, error);

    analyticsTracking.logError(
      error.name || 'unknown_error',
      error.message || 'No error message',
      context
    );
  },

  /**
   * Create an error handler function for async operations
   * @param context - Context where the error handler will be used
   * @returns A function that handles and reports errors
   */
  createErrorHandler: (context: string) => (error: Error): void => {
    errorReporting.reportError(error, context);
  }
};

/**
 * Hook to track page views in React components
 * @param pageName - Name of the page to track
 */
export const usePageTracking = (pageName: string): void => {
  // This would typically use useEffect in a React component
  // For demonstration purposes, we're just showing the function signature
  analyticsTracking.logPageView(pageName);
};

export default {
  performance: performanceMonitoring,
  analytics: analyticsTracking,
  errors: errorReporting
};

