import { Header } from '@/components/Header';
import { AssessmentForm } from '@/components/AssessmentForm';
import { BackgroundWrapper } from '@/components/BackgroundWrapper';
import { Button } from "@/components/ui/button";
import { useNavigate } from 'react-router-dom';
import { ChevronLeft } from "lucide-react";
import { useEffect } from 'react';
import { useAssessmentData } from '@/hooks/useAssessmentData';

const SecondStep = () => {
  const navigate = useNavigate();
  const { saveAssessmentData } = useAssessmentData();

  useEffect(() => {
    // Track that user has reached the second step
    const trackProgress = async () => {
      try {
        await saveAssessmentData({
          step: 'second-step',
          timestamp: new Date().toISOString()
        });
      } catch (error) {
        console.error("Error tracking progress:", error);
      }
    };

    trackProgress();
  }, [saveAssessmentData]);

  const handleBack = () => {
    navigate('/dashboard');
  };

  return (
    <BackgroundWrapper variant="state-phase" minHeight="screen" className="flex flex-col h-screen overflow-hidden">
      <Header />
      <div className="absolute top-20 left-4 right-4 z-10">
        <div className="flex justify-between items-center">
          <button
            onClick={handleBack}
            className="h-10 w-10 rounded-full backdrop-blur-xl bg-white/20 hover:bg-white/30 border border-white/30 flex items-center justify-center transition-all duration-200 hover:scale-105 active:scale-95 shadow-lg"
            aria-label="Back to dashboard"
          >
            <ChevronLeft className="h-5 w-5 text-white" />
          </button>
        </div>
      </div>
      <main className="flex-1 relative">
        <AssessmentForm />
      </main>
    </BackgroundWrapper>
  );
};

export default SecondStep;

