   308→          });
   309→          console.log('✅ Successfully saved importance to Firebase');
   310→        } catch (firebaseError) {
   311→          console.warn('⚠️ Firebase save failed, proceeding with local storage:', firebaseError);
   312→          // Continue - the data is already saved locally
