   375→              </Tooltip>
   376→            </TooltipProvider>
   377→          </div>
   378→          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
   379→            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${questions.length}`}
   380→          </span>
   381→        </div>
