   376→  return crossDeviceStorage.removeData(key);
   377→}
   378→
   379→export async function getStorageHealthStatus() {
   380→  const health = await crossDeviceStorage.getStorageHealth();
   381→  
   382→  // Generate recommendation based on storage status
   383→  let recommendation = '';
   384→  if (!health.localStorage && !health.sessionStorage && !health.indexedDB) {
   385→    recommendation = 'All storage methods are unavailable. Please check your browser settings.';
   386→  } else if (health.isIOSSafari && !health.localStorage) {
   387→    recommendation = 'iOS Safari detected with storage restrictions. Using fallback storage methods.';
   388→  } else {
   389→    recommendation = 'Storage is functioning normally.';
   390→  }
   391→  
   392→  // Add iOS storage manager info to recommendation if available
   393→  if (health.iosStorageStatus && health.isIOSSafari) {
   394→    recommendation += ` Using ${health.iosStorageStatus.primary} storage with ${health.iosStorageStatus.fallbacks.length} fallback options.`;
   395→  }
   396→  
   397→  return {
   398→    ...health,
   399→    recommendation
   400→  };
   401→}

