    36→  status: 'draft' | 'completed';
    37→  lastModified: Date;
    38→  data: Partial<AssessmentData>;
    39→}
    40→
    41→export interface SaveAssessmentOptions {
    42→  forceDraft?: boolean;
    43→  skipFirebase?: boolean;
    44→}
    45→
    46→class AssessmentService {
    47→  private readonly DRAFT_KEY = 'assessment_draft';
    48→  private readonly OFFLINE_QUEUE_KEY = 'offline_assessment_queue';
    49→
    50→  /**
    51→   * Save assessment to Firebase with automatic fallback to local storage
    52→   */
    53→  async saveAssessment(
    54→    data: Partial<AssessmentData>, 
    55→    userId?: string,
    56→    options: SaveAssessmentOptions = {}
    57→  ): Promise<string> {
    58→    debugLog.info('AssessmentService: Saving assessment', { userId, options });
    59→
    60→    // Generate assessment ID if not provided
    61→    const assessmentId = data.id || this.generateAssessmentId();
    62→    const timestamp = Timestamp.now();
    63→
    64→    // Prepare assessment data
    65→    const assessmentData: AssessmentData = {
    66→      ...data,
    67→      id: assessmentId,
    68→      userId: userId || 'anonymous',
    69→      createdAt: data.createdAt || timestamp,
    70→      updatedAt: timestamp,
    71→      completedAt: data.status === 'completed' ? timestamp : undefined,
    72→    } as AssessmentData;
    73→
    74→    // If forced draft mode or skip Firebase, only save locally
    75→    if (options.forceDraft || options.skipFirebase) {
    76→      await this.saveDraft(assessmentData);
    77→      return assessmentId;
    78→    }
    79→
    80→    try {
    81→      // Try to save to Firebase first
    82→      if (userId) {
    83→        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
    84→        await setDoc(docRef, {
    85→          ...assessmentData,
    86→          updatedAt: serverTimestamp()
    87→        });
    88→        debugLog.info('AssessmentService: Saved to Firebase successfully');
    89→
    90→        // Clear local draft after successful Firebase save
    91→        await this.clearDraft(assessmentId);
    92→      } else {
    93→        // No user ID - save as anonymous assessment
    94→        const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
    95→        await setDoc(anonRef, {
    96→          ...assessmentData,
    97→          updatedAt: serverTimestamp()
    98→        });
    99→        debugLog.info('AssessmentService: Saved as anonymous assessment');
   100→      }
   101→
   102→      // Also save to local storage for quick access
   103→      await setCrossDeviceStorageItem(
   104→        `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
   105→        assessmentData
   106→      );
   107→
   108→      return assessmentId;
   109→    } catch (error) {
   110→      debugLog.error('AssessmentService: Firebase save failed, falling back to local', error);
   111→      
   112→      // Fallback to local storage and queue for later sync
   113→      await this.saveDraft(assessmentData);
   114→      await this.queueForSync(assessmentData);
   115→      
   116→      return assessmentId;
   117→    }
   118→  }
   119→
   120→  /**
   121→   * Get assessment from Firebase with fallback to local storage
   122→   */
   123→  async getAssessment(assessmentId: string, userId?: string): Promise<AssessmentData | null> {
   124→    debugLog.info('AssessmentService: Getting assessment', { assessmentId, userId });
   125→
   126→    // Try Firebase first
   127→    if (userId) {
   128→      try {
   129→        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
   130→        const docSnap = await getDoc(docRef);
   131→        
   132→        if (docSnap.exists()) {
   133→          debugLog.info('AssessmentService: Found in Firebase');
   134→          return docSnap.data() as AssessmentData;
   135→        }
   136→      } catch (error) {
   137→        debugLog.warn('AssessmentService: Firebase fetch failed', error);
   138→      }
   139→    }
   140→
   141→    // Try anonymous assessments
   142→    try {
   143→      const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
   144→      const anonSnap = await getDoc(anonRef);
   145→      
   146→      if (anonSnap.exists()) {
   147→        debugLog.info('AssessmentService: Found in anonymous assessments');
   148→        return anonSnap.data() as AssessmentData;
   149→      }
   150→    } catch (error) {
   151→      debugLog.warn('AssessmentService: Anonymous fetch failed', error);
   152→    }
   153→
   154→    // Fallback to local storage
   155→    const localData = await getCrossDeviceStorageItem<AssessmentData>(
   156→      `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
   157→      null
   158→    );
   159→
   160→    if (localData) {
   161→      debugLog.info('AssessmentService: Found in local storage');
   162→      return localData;
   163→    }
   164→
   165→    // Check drafts
   166→    const draft = await this.getDraft(assessmentId);
   167→    if (draft) {
   168→      debugLog.info('AssessmentService: Found in drafts');
   169→      return draft.data as AssessmentData;
   170→    }
   171→
   172→    return null;
   173→  }
   174→
   175→  /**
   176→   * Get the most recent assessment results (for results page)
   177→   */
   178→  async getMostRecentResults(userId?: string): Promise<AssessmentData | null> {
   179→    debugLog.info('AssessmentService: Getting most recent results', { userId });
   180→
   181→    // First check if we have a recently completed assessment in local storage
   182→    const recentLocal = await getCrossDeviceStorageItem<AssessmentData>(
   183→      STORAGE_KEYS.JOB_SATISFACTION_RESULTS,
   184→      null
   185→    );
   186→
   187→    if (recentLocal && recentLocal.completedAt) {
   188→      debugLog.info('AssessmentService: Found recent results in local storage');
   189→      return recentLocal;
   190→    }
   191→
   192→    // Try Firebase if we have a user
   193→    if (userId) {
   194→      try {
   195→        // Query for completed assessments - check both new 'status' and legacy 'step' fields
   196→        const q = query(
   197→          collection(firestore, 'users', userId, 'assessments'),
   198→          orderBy('completedAt', 'desc'),
   199→          limit(10) // Get more to filter client-side for compatibility
   200→        );
   201→        
   202→        const querySnapshot = await getDocs(q);
   203→        if (!querySnapshot.empty) {
   204→          // Filter for completed assessments (check both status and step for compatibility)
   205→          const completedAssessments = querySnapshot.docs
   206→            .map(doc => ({ id: doc.id, ...doc.data() } as AssessmentData))
   207→            .filter(assessment => 
   208→              (assessment.status === 'completed' || assessment.step === 'results') &&
   209→              assessment.jobSatisfactionResults
   210→            );
   211→          
   212→          if (completedAssessments.length > 0) {
   213→            const data = completedAssessments[0];
   214→            debugLog.info('AssessmentService: Found recent results in Firebase');
   215→            
   216→            // Cache locally for faster access
   217→            await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, data);
   218→            
   219→            return data;
   220→          }
   221→        }
   222→      } catch (error) {
   223→        debugLog.warn('AssessmentService: Firebase query failed', error);
   224→      }
   225→    }
   226→
   227→    // Check anonymous assessments
   228→    try {
   229→      // For anonymous, we'll check local storage for the most recent
   230→      const allLocalKeys = ['JOB_SATISFACTION_RESULTS', 'assessment_draft'];
   231→      for (const key of allLocalKeys) {
   232→        const data = await getCrossDeviceStorageItem<any>(key, null);
   233→        if (data && data.jobSatisfactionResults) {
   234→          debugLog.info('AssessmentService: Found results in local storage');
   235→          return data;
   236→        }
   237→      }
   238→    } catch (error) {
   239→      debugLog.warn('AssessmentService: Local storage check failed', error);
   240→    }
   241→
   242→    return null;
   243→  }
   244→
   245→  /**
   246→   * Update assessment draft (for saving progress)
   247→   */
   248→  async updateDraft(assessmentId: string, data: Partial<AssessmentData>): Promise<void> {
   249→    const existing = await this.getDraft(assessmentId);
   250→    const draft: AssessmentDraft = {
   251→      id: assessmentId,
   252→      status: 'draft',
   253→      lastModified: new Date(),
   254→      data: {
   255→        ...(existing?.data || {}),
