const handleSubmit = async () => {
  const unansweredIndex = answers.findIndex(answer => answer.rating === null);
  if (unansweredIndex !== -1) {
    setCurrentQuestionIndex(unansweredIndex);
    toast({
      title: "Incomplete Assessment",
      description: `Please rate all aspects. Going to question ${unansweredIndex + 1}`,
      variant: "destructive"
    });
    return;
  }

  setIsSubmitting(true);

  // Add timeout to prevent infinite processing
  const OVERALL_TIMEOUT = 30000; // 30 seconds max
  let timeoutId: NodeJS.Timeout | null = null;

  try {
    // Set overall timeout to prevent hanging
    const overallTimeout = new Promise((_, reject) => {
      timeoutId = setTimeout(() => {
        reject(new Error('Overall submission timeout - process taking too long'));
      }, OVERALL_TIMEOUT);
    });

    // Main submission logic with overall timeout
    await Promise.race([overallTimeout, submitLogic()]);
  } catch (error) {
    console.error("Error in handleSubmit:", error);
    toast({
      title: "Submission Error",
      description: error instanceof Error ? error.message : "An unexpected error occurred. Please try again.",
      variant: "destructive"
    });
  } finally {
    if (timeoutId) clearTimeout(timeoutId);
    setIsSubmitting(false);
  }
};

