   308→    }
   309→  }
   310→
   311→  /**
   312→   * Queue assessment for sync when online
   313→   */
   314→  private async queueForSync(data: AssessmentData): Promise<void> {
   315→    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
   316→      this.OFFLINE_QUEUE_KEY,
   317→      []
   318→    );
   319→    
   320→    // Add to queue if not already there
   321→    const exists = queue.some(item => item.id === data.id);
   322→    if (!exists) {
   323→      queue.push(data);
   324→      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, queue);
   325→      debugLog.info('AssessmentService: Queued for sync', { id: data.id });
   326→    }
   327→  }
   328→
   329→  /**
   330→   * Process offline queue
   331→   */
   332→  async syncOfflineQueue(userId?: string): Promise<void> {
   333→    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
   334→      this.OFFLINE_QUEUE_KEY,
   335→      []
   336→    );
   337→
   338→    if (queue.length === 0) return;
   339→
   340→    debugLog.info('AssessmentService: Processing offline queue', { count: queue.length });
   341→
   342→    const remaining: AssessmentData[] = [];
   343→
   344→    for (const assessment of queue) {
   345→      try {
   346→        await this.saveAssessment(assessment, userId || assessment.userId);
   347→        debugLog.info('AssessmentService: Synced assessment', { id: assessment.id });
   348→      } catch (error) {
   349→        debugLog.error('AssessmentService: Sync failed, keeping in queue', error);
   350→        remaining.push(assessment);
   351→      }
   352→    }
   353→
   354→    // Update queue with remaining items
   355→    if (remaining.length > 0) {
   356→      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, remaining);
   357→    } else {
   358→      await removeCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY);
   359→    }
   360→  }
   361→
   362→  /**
   363→   * Generate unique assessment ID
   364→   */
   365→  private generateAssessmentId(): string {
   366→    return `assessment_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   367→  }
   368→
   369→  /**
   370→   * Check if we have network connectivity
   371→   */
   372→  private isOnline(): boolean {
   373→    return navigator.onLine;
   374→  }
   375→}
   376→
   377→// Export singleton instance
   378→export const assessmentService = new AssessmentService();

