/**
 * Firebase Assessment Service
 * 
 * This service handles all assessment data operations with Firebase as the primary source of truth.
 * It includes fallback mechanisms for offline and private browsing scenarios.
 */

import { 
  collection, 
  doc, 
  setDoc, 
  getDoc, 
  updateDoc, 
  query, 
  where, 
  orderBy, 
  limit, 
  getDocs,
  Timestamp,
  serverTimestamp
} from 'firebase/firestore';
import { firestore } from '@/firebase/config';
import { AssessmentData } from '@/models/AssessmentModels';
import { debugLog } from '@/utils/debugLogger';
import { 
  getCrossDeviceStorageItem, 
  setCrossDeviceStorageItem,
  removeCrossDeviceStorageItem 
} from '@/utils/crossDeviceStorage';
import { STORAGE_KEYS } from '@/utils/storageUtils';

// Types for service operations
export interface AssessmentDraft {
  id: string;
  userId?: string;
  status: 'draft' | 'completed';
  lastModified: Date;
  data: Partial<AssessmentData>;
}

export interface SaveAssessmentOptions {
  forceDraft?: boolean;
  skipFirebase?: boolean;
}

class AssessmentService {
  private readonly DRAFT_KEY = 'assessment_draft';
  private readonly OFFLINE_QUEUE_KEY = 'offline_assessment_queue';

  /**
   * Save assessment to Firebase with automatic fallback to local storage
   */
  async saveAssessment(
    data: Partial<AssessmentData>, 
    userId?: string,
    options: SaveAssessmentOptions = {}
  ): Promise<string> {
    debugLog.info('AssessmentService: Saving assessment', { userId, options });

    // Generate assessment ID if not provided
    const assessmentId = data.id || this.generateAssessmentId();
    const timestamp = Timestamp.now();

    // Prepare assessment data
    const assessmentData: AssessmentData = {
      ...data,
      id: assessmentId,
      userId: userId || 'anonymous',
      createdAt: data.createdAt || timestamp,
      updatedAt: timestamp,
      completedAt: data.status === 'completed' ? timestamp : undefined,
    } as AssessmentData;

    // If forced draft mode or skip Firebase, only save locally
    if (options.forceDraft || options.skipFirebase) {
      await this.saveDraft(assessmentData);
      return assessmentId;
    }

    try {
      // Try to save to Firebase first
      if (userId) {
        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
        await setDoc(docRef, {
          ...assessmentData,
          updatedAt: serverTimestamp()
        });
        debugLog.info('AssessmentService: Saved to Firebase successfully');

        // Clear local draft after successful Firebase save
        await this.clearDraft(assessmentId);
      } else {
        // No user ID - save as anonymous assessment
        const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
        await setDoc(anonRef, {
          ...assessmentData,
          updatedAt: serverTimestamp()
        });
        debugLog.info('AssessmentService: Saved as anonymous assessment');
      }

      // Also save to local storage for quick access
      await setCrossDeviceStorageItem(
        `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
        assessmentData
      );

      return assessmentId;
    } catch (error) {
      debugLog.error('AssessmentService: Firebase save failed, falling back to local', error);

      // Fallback to local storage and queue for later sync
      await this.saveDraft(assessmentData);
      await this.queueForSync(assessmentData);

      return assessmentId;
    }
  }

  /**
   * Get assessment from Firebase with fallback to local storage
   */
  async getAssessment(assessmentId: string, userId?: string): Promise<AssessmentData | null> {
    debugLog.info('AssessmentService: Getting assessment', { assessmentId, userId });

    // Try Firebase first
    if (userId) {
      try {
        const docRef = doc(firestore, 'users', userId, 'assessments', assessmentId);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          debugLog.info('AssessmentService: Found in Firebase');
          return docSnap.data() as AssessmentData;
        }
      } catch (error) {
        debugLog.warn('AssessmentService: Firebase fetch failed', error);
      }
    }

    // Try anonymous assessments
    try {
      const anonRef = doc(firestore, 'anonymous_assessments', assessmentId);
      const anonSnap = await getDoc(anonRef);

      if (anonSnap.exists()) {
        debugLog.info('AssessmentService: Found in anonymous assessments');
        return anonSnap.data() as AssessmentData;
      }
    } catch (error) {
      debugLog.warn('AssessmentService: Anonymous fetch failed', error);
    }

    // Fallback to local storage
    const localData = await getCrossDeviceStorageItem<AssessmentData>(
      `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${assessmentId}`,
      null
    );

    if (localData) {
      debugLog.info('AssessmentService: Found in local storage');
      return localData;
    }

    // Check drafts
    const draft = await this.getDraft(assessmentId);
    if (draft) {
      debugLog.info('AssessmentService: Found in drafts');
      return draft.data as AssessmentData;
    }

    return null;
  }

  /**
   * Get the most recent assessment results (for results page)
   */
  async getMostRecentResults(userId?: string): Promise<AssessmentData | null> {
    debugLog.info('AssessmentService: Getting most recent results', { userId });

    // First check if we have a recently completed assessment in local storage
    const recentLocal = await getCrossDeviceStorageItem<AssessmentData>(
      STORAGE_KEYS.JOB_SATISFACTION_RESULTS,
      null
    );

    if (recentLocal && recentLocal.completedAt) {
      debugLog.info('AssessmentService: Found recent results in local storage');
      return recentLocal;
    }

    // Try Firebase if we have a user
    if (userId) {
      try {
        // Query for completed assessments - check both new 'status' and legacy 'step' fields
        const q = query(
          collection(firestore, 'users', userId, 'assessments'),
          orderBy('completedAt', 'desc'),
          limit(10) // Get more to filter client-side for compatibility
        );

        const querySnapshot = await getDocs(q);
        if (!querySnapshot.empty) {
          // Filter for completed assessments (check both status and step for compatibility)
          const completedAssessments = querySnapshot.docs
            .map(doc => ({ id: doc.id, ...doc.data() } as AssessmentData))
            .filter(assessment => 
              (assessment.status === 'completed' || assessment.step === 'results') &&
              assessment.jobSatisfactionResults
            );

          if (completedAssessments.length > 0) {
            const data = completedAssessments[0];
            debugLog.info('AssessmentService: Found recent results in Firebase');

            // Cache locally for faster access
            await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, data);

            return data;
          }
        }
      } catch (error) {
        debugLog.warn('AssessmentService: Firebase query failed', error);
      }
    }

    // Check anonymous assessments
    try {
      // For anonymous, we'll check local storage for the most recent
      const allLocalKeys = ['JOB_SATISFACTION_RESULTS', 'assessment_draft'];
      for (const key of allLocalKeys) {
        const data = await getCrossDeviceStorageItem<any>(key, null);
        if (data && data.jobSatisfactionResults) {
          debugLog.info('AssessmentService: Found results in local storage');
          return data;
        }
      }
    } catch (error) {
      debugLog.warn('AssessmentService: Local storage check failed', error);
    }

    return null;
  }

  /**
   * Update assessment draft (for saving progress)
   */
  async updateDraft(assessmentId: string, data: Partial<AssessmentData>): Promise<void> {
    const existing = await this.getDraft(assessmentId);
    const draft: AssessmentDraft = {
      id: assessmentId,
      status: 'draft',
      lastModified: new Date(),
      data: {
        ...(existing?.data || {}),
        ...data,
        id: assessmentId,
        updatedAt: Timestamp.now()
      }
    };

    await setCrossDeviceStorageItem(this.DRAFT_KEY, draft);
    debugLog.info('AssessmentService: Draft updated', { assessmentId });
  }

  /**
   * Save draft locally
   */
  private async saveDraft(data: AssessmentData): Promise<void> {
    const draft: AssessmentDraft = {
      id: data.id!,
      userId: data.userId,
      status: data.status || 'draft',
      lastModified: new Date(),
      data
    };

    await setCrossDeviceStorageItem(this.DRAFT_KEY, draft);
    await setCrossDeviceStorageItem(
      `${STORAGE_KEYS.JOB_SATISFACTION_RESULTS}_${data.id}`,
      data
    );

    // Also save to the standard location for backward compatibility
    if (data.jobSatisfactionResults) {
      await setCrossDeviceStorageItem(
        STORAGE_KEYS.JOB_SATISFACTION_RESULTS,
        data.jobSatisfactionResults
      );
    }
  }

  /**
   * Get draft from local storage
   */
  private async getDraft(assessmentId: string): Promise<AssessmentDraft | null> {
    const draft = await getCrossDeviceStorageItem<AssessmentDraft>(this.DRAFT_KEY, null);
    return draft && draft.id === assessmentId ? draft : null;
  }

  /**
   * Clear draft after successful save
   */
  private async clearDraft(assessmentId: string): Promise<void> {
    const draft = await this.getDraft(assessmentId);
    if (draft && draft.id === assessmentId) {
      await removeCrossDeviceStorageItem(this.DRAFT_KEY);
    }
  }

  /**
   * Queue assessment for sync when online
   */
  private async queueForSync(data: AssessmentData): Promise<void> {
    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
      this.OFFLINE_QUEUE_KEY,
      []
    );

    // Add to queue if not already there
    const exists = queue.some(item => item.id === data.id);
    if (!exists) {
      queue.push(data);
      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, queue);
      debugLog.info('AssessmentService: Queued for sync', { id: data.id });
    }
  }

  /**
   * Process offline queue
   */
  async syncOfflineQueue(userId?: string): Promise<void> {
    const queue = await getCrossDeviceStorageItem<AssessmentData[]>(
      this.OFFLINE_QUEUE_KEY,
      []
    );

    if (queue.length === 0) return;

    debugLog.info('AssessmentService: Processing offline queue', { count: queue.length });

    const remaining: AssessmentData[] = [];

    for (const assessment of queue) {
      try {
        await this.saveAssessment(assessment, userId || assessment.userId);
        debugLog.info('AssessmentService: Synced assessment', { id: assessment.id });
      } catch (error) {
        debugLog.error('AssessmentService: Sync failed, keeping in queue', error);
        remaining.push(assessment);
      }
    }

    // Update queue with remaining items
    if (remaining.length > 0) {
      await setCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY, remaining);
    } else {
      await removeCrossDeviceStorageItem(this.OFFLINE_QUEUE_KEY);
    }
  }

  /**
   * Generate unique assessment ID
   */
  private generateAssessmentId(): string {
    return `assessment_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Check if we have network connectivity
   */
  private isOnline(): boolean {
    return navigator.onLine;
  }
}

// Export singleton instance
export const assessmentService = new AssessmentService();

