// Quick Firebase connection test
import { initializeApp } from 'firebase/app';
import { 
  initializeFirestore, 
  connectFirestoreEmulator,
  doc,
  setDoc,
  getDoc
} from 'firebase/firestore';
import { 
  getAuth, 
  connectAuthEmulator
} from 'firebase/auth';

const firebaseConfig = {
  apiKey: "AIzaSyACfdzl67B0ijRnNEp06xEYMo3wEiOas2I",
  authDomain: "jobjoy-e14e5.firebaseapp.com",
  projectId: "jobjoy-e14e5",
  storageBucket: "jobjoy-e14e5.firebasestorage.app",
  messagingSenderId: "513005463096",
  appId: "1:513005463096:web:5474e091ba0932e5ebe8cd",
  measurementId: "G-K8S3W3GQ9G"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize services
const auth = getAuth(app);
const firestore = initializeFirestore(app, {});

// Connect to emulators
try {
  connectFirestoreEmulator(firestore, 'localhost', 8785);
  connectAuthEmulator(auth, 'http://localhost:9797');
  console.log('✅ Connected to Firebase emulators');
} catch (error) {
  console.error('❌ Failed to connect to emulators:', error);
}

// Test Firestore write/read
async function testFirestore() {
  try {
    console.log('🧪 Testing Firestore connection...');

    const testDoc = doc(firestore, 'test', 'connectivity');
    const testData = {
      message: 'Hello from test',
      timestamp: new Date().toISOString()
    };

    console.log('📝 Writing test document...');
    await setDoc(testDoc, testData);
    console.log('✅ Write successful');

    console.log('📖 Reading test document...');
    const docSnap = await getDoc(testDoc);

    if (docSnap.exists()) {
      console.log('✅ Read successful:', docSnap.data());
    } else {
      console.log('❌ Document not found');
    }

    console.log('🎉 Firestore test completed successfully');
  } catch (error) {
    console.error('❌ Firestore test failed:', error);
  }
}

// Run the test
testFirestore();

