rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // Helper functions
    function isAuthenticated() {
      return request.auth != null;
    }

    function isOwner(userId) {
      return isAuthenticated() && request.auth.uid == userId;
    }

    function isAdmin() {
      return isAuthenticated() && 
        exists(/databases/$(database)/documents/users/$(request.auth.uid)) &&
        get(/databases/$(database)/documents/users/$(request.auth.uid)).data.role == 'admin';
    }

    function isValidUser() {
      return isAuthenticated() && 
        exists(/databases/$(database)/documents/users/$(request.auth.uid));
    }

    // User profiles
    match /users/{userId} {
      // Users can read and update their own profile
      allow read: if isOwner(userId) || isAdmin();
      allow create: if isAuthenticated() && request.auth.uid == userId;
      allow update: if isOwner(userId) || isAdmin();
      allow delete: if isAdmin();

      // User preferences subcollection
      match /preferences/{docId} {
        allow read, write: if isOwner(userId);
      }

      // Assessment subcollection
      match /assessments/{assessmentId} {
        allow read, write: if isOwner(userId);
      }
    }

    // Todo items
    match /todos/{todoId} {
      // Users can read, create, update, and delete their own todos
      allow read: if isValidUser() && (
        resource.data.userId == request.auth.uid || 
        resource.data.assignedTo.hasAny([request.auth.uid])
      );
      allow create: if isValidUser() && request.resource.data.userId == request.auth.uid;
      allow update: if isValidUser() && (
        resource.data.userId == request.auth.uid || 
        resource.data.assignedTo.hasAny([request.auth.uid])
      );
      allow delete: if isValidUser() && resource.data.userId == request.auth.uid;

      // Comments subcollection
      match /comments/{commentId} {
        allow read: if isValidUser();
        allow create: if isValidUser() && request.resource.data.userId == request.auth.uid;
        allow update, delete: if isValidUser() && resource.data.userId == request.auth.uid;
      }

      // Attachments subcollection
      match /attachments/{attachmentId} {
        allow read: if isValidUser();
        allow create: if isValidUser() && request.resource.data.uploadedBy == request.auth.uid;
        allow update, delete: if isValidUser() && resource.data.uploadedBy == request.auth.uid;
      }
    }

    // Categories
    match /categories/{categoryId} {
      allow read: if isValidUser();
      allow create: if isValidUser() && request.resource.data.userId == request.auth.uid;
      allow update, delete: if isValidUser() && resource.data.userId == request.auth.uid;
    }

    // Comments
    match /comments/{commentId} {
      allow read: if isValidUser();
      allow create: if isValidUser() && request.resource.data.userId == request.auth.uid;
      allow update, delete: if isValidUser() && resource.data.userId == request.auth.uid;
    }

    // Attachments
    match /attachments/{attachmentId} {
      allow read: if isValidUser();
      allow create: if isValidUser() && request.resource.data.uploadedBy == request.auth.uid;
      allow update, delete: if isValidUser() && resource.data.uploadedBy == request.auth.uid;
    }

    // Activity logs
    match /activityLogs/{logId} {
      allow read: if isValidUser();
      allow create: if isValidUser() && request.resource.data.userId == request.auth.uid;
      allow update, delete: if false; // Activity logs should not be modified or deleted
    }

    // Industry benchmarks - public read access for all users
    match /industryBenchmarks/{benchmarkId} {
      allow read: if true; // Public data - anyone can read industry benchmarks
      allow write: if isAdmin(); // Only admins can update benchmarks
    }

    // Company benchmarks - public read access for all users
    match /companyBenchmarks/{benchmarkId} {
      allow read: if true; // Public data - anyone can read company benchmarks
      allow write: if isAdmin(); // Only admins can update benchmarks
    }

    // Assessments collection - users can only access their own assessments
    match /assessments/{assessmentId} {
      // Users can read their own assessments
      allow read: if isAuthenticated() && 
        (resource.data.userId == request.auth.uid || isAdmin());

      // Users can create assessments with their own userId
      allow create: if isAuthenticated() && 
        request.resource.data.userId == request.auth.uid;

      // Users can update their own assessments
      allow update: if isAuthenticated() && 
        resource.data.userId == request.auth.uid &&
        request.resource.data.userId == request.auth.uid; // Prevent changing userId

      // Only admins can delete assessments
      allow delete: if isAdmin();
    }
  }
}

