// Test anonymous_assessments connection
import { initializeApp } from 'firebase/app';
import { 
  initializeFirestore, 
  connectFirestoreEmulator,
  doc,
  setDoc,
  getDoc
} from 'firebase/firestore';
import { 
  getAuth, 
  connectAuthEmulator
} from 'firebase/auth';

const firebaseConfig = {
  apiKey: "AIzaSyACfdzl67B0ijRnNEp06xEYMo3wEiOas2I",
  authDomain: "jobjoy-e14e5.firebaseapp.com",
  projectId: "jobjoy-e14e5",
  storageBucket: "jobjoy-e14e5.firebasestorage.app",
  messagingSenderId: "513005463096",
  appId: "1:513005463096:web:5474e091ba0932e5ebe8cd",
  measurementId: "G-K8S3W3GQ9G"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize services
const auth = getAuth(app);
const firestore = initializeFirestore(app, {});

// Connect to emulators
try {
  connectFirestoreEmulator(firestore, 'localhost', 8785);
  connectAuthEmulator(auth, 'http://localhost:9797');
  console.log('✅ Connected to Firebase emulators');
} catch (error) {
  console.error('❌ Failed to connect to emulators:', error);
}

// Test anonymous_assessments write/read
async function testAnonymousAssessments() {
  try {
    console.log('🧪 Testing anonymous_assessments connection...');

    const assessmentId = `assessment_${Date.now()}_test`;
    const testDoc = doc(firestore, 'anonymous_assessments', assessmentId);
    const testData = {
      id: assessmentId,
      userId: 'anonymous',
      status: 'draft',
      createdAt: new Date(),
      updatedAt: new Date(),
      step: 'importance_completed'
    };

    console.log('📝 Writing to anonymous_assessments...');
    await setDoc(testDoc, testData);
    console.log('✅ Write successful');

    console.log('📖 Reading from anonymous_assessments...');
    const docSnap = await getDoc(testDoc);

    if (docSnap.exists()) {
      console.log('✅ Read successful:', docSnap.data());
    } else {
      console.log('❌ Document not found');
    }

    console.log('🎉 Anonymous assessments test completed successfully');
  } catch (error) {
    console.error('❌ Anonymous assessments test failed:', error);
  }
}

// Run the test
testAnonymousAssessments();

