/**
 * Get the Firebase configuration from environment variables
 * 
 * CRITICAL: Project ID consistency is required to prevent Firebase emulator errors.
 * The projectId MUST match across:
 * - .firebaserc (default project)
 * - .env.development (development environment)
 * - .env (production environment)
 * 
 * Mismatched project IDs cause "Multiple projectIds are not recommended" 
 * errors and result in Firebase operation timeouts.
 * 
 * @returns The Firebase configuration object
 */
export const getFirebaseConfig = () => {
  const config = {
    apiKey: getEnvVariable('VITE_FIREBASE_API_KEY'),
    authDomain: getEnvVariable('VITE_FIREBASE_AUTH_DOMAIN'),
    projectId: getEnvVariable('VITE_FIREBASE_PROJECT_ID'),
    storageBucket: getEnvVariable('VITE_FIREBASE_STORAGE_BUCKET'),
    messagingSenderId: getEnvVariable('VITE_FIREBASE_MESSAGING_SENDER_ID'),
    appId: getEnvVariable('VITE_FIREBASE_APP_ID'),
    measurementId: getEnvVariable('VITE_FIREBASE_MEASUREMENT_ID'),
  };

  // Development debugging: Log project ID to help identify mismatches
  if (isDevelopment()) {
    console.log(`[Firebase Config] Project ID: ${config.projectId}`);
    console.log(`[Firebase Config] Environment: ${getEnvironment()}`);
  }

  return config;
};

