// Environment Utility
// This file provides utilities for environment configuration

/**
 * Get the current environment
 * @returns The current environment (development, test, production)
 */
export const getEnvironment = (): string => {
  return import.meta.env.MODE || 'development';
};

/**
 * Check if the current environment is development
 * @returns True if the current environment is development
 */
export const isDevelopment = (): boolean => {
  return getEnvironment() === 'development';
};

/**
 * Check if the current environment is test
 * @returns True if the current environment is test
 */
export const isTest = (): boolean => {
  return getEnvironment() === 'test';
};

/**
 * Check if the current environment is production
 * @returns True if the current environment is production
 */
export const isProduction = (): boolean => {
  return getEnvironment() === 'production';
};

/**
 * Get an environment variable
 * @param key The environment variable key
 * @param defaultValue The default value if the environment variable is not set
 * @returns The environment variable value or the default value
 */
export const getEnvVariable = (key: string, defaultValue: string = ''): string => {
  return import.meta.env[key] || defaultValue;
};

