/**
 * Safe utility functions for working with localStorage
 */

/**
 * Check if localStorage is available
 * @returns boolean indicating if localStorage is available
 */
export const isLocalStorageAvailable = (): boolean => {
  try {
    const testKey = '__test__';
    localStorage.setItem(testKey, testKey);
    localStorage.removeItem(testKey);
    return true;
  } catch (e) {
    return false;
  }
};

/**
 * Safely get an item from localStorage with proper type conversion
 * @param key The key to retrieve from localStorage
 * @param defaultValue Default value to return if item doesn't exist or if there's an error
 * @returns The parsed value or defaultValue if not found/error
 */
export function getStorageItem<T>(key: string, defaultValue: T): T {
  if (!isLocalStorageAvailable()) {
    return defaultValue;
  }

  try {
    const item = localStorage.getItem(key);
    if (item === null) {
      return defaultValue;
    }
    return JSON.parse(item) as T;
  } catch (error) {
    console.error(`Error getting item [${key}] from localStorage:`, error);
    return defaultValue;
  }
}

/**
 * Safely set an item in localStorage with proper serialization
 * @param key The key to set in localStorage
 * @param value The value to store
 * @returns boolean indicating success
 */
export function setStorageItem<T>(key: string, value: T): boolean {
  if (!isLocalStorageAvailable()) {
    return false;
  }

  try {
    localStorage.setItem(key, JSON.stringify(value));
    return true;
  } catch (error) {
    console.error(`Error setting item [${key}] in localStorage:`, error);
    return false;
  }
}

/**
 * Safely remove an item from localStorage
 * @param key The key to remove from localStorage
 * @returns boolean indicating success
 */
export function removeStorageItem(key: string): boolean {
  if (!isLocalStorageAvailable()) {
    return false;
  }

  try {
    localStorage.removeItem(key);
    return true;
  } catch (error) {
    console.error(`Error removing item [${key}] from localStorage:`, error);
    return false;
  }
}

/**
 * Storage keys used in the application
 * Centralizing them prevents typos and inconsistencies
 */
export const STORAGE_KEYS = {
  IMPORTANCE_RESULTS: 'importanceResults',
  IMPORTANCE_SKIPPED: 'importanceSkipped',
  JOB_SATISFACTION_RESULTS: 'jobSatisfactionResults',
  REDIRECT_TO_RESULTS: 'redirectToResults',
  SATISFACTION_RESPONSES: 'satisfactionResponses',
  SAVE_DIALOG_SHOWN: 'saveDialogShown'
};

