   190→    this.fallbackStorages = storages.slice(1);
   191→
   192→    debugLog.info('LayeredStorageManager initialized', {
   193→      primary: this.primaryStorage.type,
   194→      fallbacks: this.fallbackStorages.map(s => s.type)
   195→    });
   196→  }
   197→
   198→  private testStorage(type: 'localStorage' | 'sessionStorage'): boolean {
