   218→    } catch (error) {
   219→      return false;
   220→    }
   221→  }
   222→
   223→  async setItem(key: string, value: string): Promise<void> {
   224→    // Try primary storage first
   225→    try {
   226→      await this.primaryStorage.setItem(key, value);
   227→      
   228→      // Also save to first fallback for redundancy
   229→      if (this.fallbackStorages.length > 0) {
   230→        try {
   231→          await this.fallbackStorages[0].setItem(key, value);
   232→        } catch (fallbackError) {
