   252→    }
   253→  }
   254→
   255→  async getItem(key: string): Promise<string | null> {
   256→    // Try primary storage first
   257→    try {
   258→      const value = await this.primaryStorage.getItem(key);
   259→      if (value !== null) return value;
   260→    } catch (error) {
   261→      debugLog.warn(`Primary storage (${this.primaryStorage.type}) read failed`, error);
   262→    }
   263→
   264→    // Try fallbacks
   265→    for (const storage of this.fallbackStorages) {
   266→      try {
   267→        const value = await storage.getItem(key);
   268→        if (value !== null) {
