   290→    await Promise.all(promises);
   291→  }
   292→
   293→  async clear(): Promise<void> {
   294→    // Clear all storages
   295→    const promises = [this.primaryStorage, ...this.fallbackStorages].map(async storage => {
   296→      try {
   297→        await storage.clear();
   298→      } catch (error) {
   299→        debugLog.warn(`Failed to clear ${storage.type}`, error);
   300→      }
   301→    });
   302→
   303→    await Promise.all(promises);
   304→  }
   305→
   306→  // Get current storage status for debugging
   307→  getStorageStatus(): {
   308→    primary: string;
   309→    fallbacks: string[];
   310→    available: string[];
   311→  } {
   312→    return {
   313→      primary: this.primaryStorage.type,
   314→      fallbacks: this.fallbackStorages.map(s => s.type),
   315→      available: [this.primaryStorage, ...this.fallbackStorages].map(s => s.type)
   316→    };
   317→  }
   318→}
   319→
   320→/**
   321→ * Storage type detection based on data sensitivity
   322→ */
   323→export type DataSensitivity = 'sensitive' | 'preference' | 'cache';
   324→
   325→export function getStorageForDataType(dataType: DataSensitivity): StorageInterface {
   326→  switch (dataType) {
   327→    case 'sensitive':
   328→      // Never persist sensitive data - memory only
   329→      return new MemoryStorage();
   330→    
   331→    case 'preference':
   332→      // Use best available storage
   333→      return new LayeredStorageManager();
   334→    
   335→    case 'cache':
   336→      // Use whatever is available, no guarantees
   337→      return new LayeredStorageManager();
   338→    
   339→    default:
   340→      return new MemoryStorage();
   341→  }
   342→}
   343→
   344→/**
   345→ * iOS-optimized storage hook for React components
   346→ */
   347→export function useIOSStorage<T>(
   348→  key: string, 
   349→  initialValue: T,
   350→  sensitivity: DataSensitivity = 'preference'
   351→): [T, (value: T) => Promise<void>, () => Promise<void>] {
   352→  const storage = getStorageForDataType(sensitivity);
   353→  
   354→  const [storedValue, setStoredValue] = React.useState<T>(() => {
   355→    try {
   356→      const item = storage.getItem(key);
   357→      if (item && typeof item === 'string') {
   358→        return JSON.parse(item);
   359→      }
   360→    } catch (error) {
   361→      debugLog.error('Failed to load stored value', error);
   362→    }
   363→    return initialValue;
   364→  });
   365→
   366→  const setValue = async (value: T) => {
   367→    try {
   368→      setStoredValue(value);
   369→      await storage.setItem(key, JSON.stringify(value));
   370→    } catch (error) {
   371→      debugLog.error('Failed to save value', error);
   372→      throw error;
   373→    }
   374→  };
   375→
   376→  const removeValue = async () => {
   377→    try {
   378→      setStoredValue(initialValue);
   379→      await storage.removeItem(key);
   380→    } catch (error) {
   381→      debugLog.error('Failed to remove value', error);
   382→      throw error;
   383→    }
   384→  };
   385→
   386→  return [storedValue, setValue, removeValue];
   387→}
   388→
   389→// Export singleton instance for global use
   390→export const iosStorage = new LayeredStorageManager();
   391→
   392→// Also need React import for the hook
   393→import React from 'react';

