/**
 * iOS Storage Manager - Enhanced storage strategy for iOS Safari compatibility
 * 
 * This module implements a layered storage approach optimized for iOS Safari,
 * particularly for private browsing mode compatibility.
 * 
 * Storage Hierarchy:
 * 1. localStorage (when available)
 * 2. sessionStorage (iOS private browsing)
 * 3. Cookies (session-only, no persistence)
 * 4. Memory storage (ultimate fallback)
 */

import Cookies from 'js-cookie';
import { debugLog } from './debugLogger';

// Storage interface for consistent API across all storage types
export interface StorageInterface {
  setItem(key: string, value: string): void | Promise<void>;
  getItem(key: string): string | null | Promise<string | null>;
  removeItem(key: string): void | Promise<void>;
  clear(): void | Promise<void>;
  readonly type: string;
}

/**
 * Memory Storage Class - Ultimate fallback for complete storage failure
 */
export class MemoryStorage implements StorageInterface {
  private storage: Record<string, string> = {};
  readonly type = 'memory';

  setItem(key: string, value: string): void {
    this.storage[key] = value;
    debugLog.info(`MemoryStorage: Set ${key}`);
  }

  getItem(key: string): string | null {
    return this.storage[key] || null;
  }

  removeItem(key: string): void {
    delete this.storage[key];
    debugLog.info(`MemoryStorage: Removed ${key}`);
  }

  clear(): void {
    this.storage = {};
    debugLog.info('MemoryStorage: Cleared all data');
  }

  // Get current memory usage for monitoring
  getSize(): number {
    return Object.keys(this.storage).length;
  }
}

/**
 * Cookie Storage Class - Fallback for iOS private browsing
 */
export class CookieStorage implements StorageInterface {
  readonly type = 'cookie';

  setItem(key: string, value: string): void {
    try {
      // Session cookies only - don't persist after browser close
      // Use strict SameSite for security
      Cookies.set(key, value, { 
        sameSite: 'strict',
        secure: window.location.protocol === 'https:' // Only secure in production
      });
      debugLog.info(`CookieStorage: Set ${key}`);
    } catch (error) {
      debugLog.error('CookieStorage: Failed to set cookie', error);
      throw error;
    }
  }

  getItem(key: string): string | null {
    try {
      return Cookies.get(key) || null;
    } catch (error) {
      debugLog.error('CookieStorage: Failed to get cookie', error);
      return null;
    }
  }

  removeItem(key: string): void {
    try {
      Cookies.remove(key);
      debugLog.info(`CookieStorage: Removed ${key}`);
    } catch (error) {
      debugLog.error('CookieStorage: Failed to remove cookie', error);
    }
  }

  clear(): void {
    try {
      // Get all cookies and remove them
      const allCookies = Cookies.get();
      Object.keys(allCookies).forEach(key => {
        Cookies.remove(key);
      });
      debugLog.info('CookieStorage: Cleared all cookies');
    } catch (error) {
      debugLog.error('CookieStorage: Failed to clear cookies', error);
    }
  }
}

/**
 * Native Storage Wrapper - Wraps browser storage APIs with error handling
 */
class NativeStorageWrapper implements StorageInterface {
  constructor(
    private storage: Storage,
    public readonly type: string
  ) {}

  setItem(key: string, value: string): void {
    try {
      this.storage.setItem(key, value);
    } catch (error) {
      debugLog.error(`${this.type}: Failed to set item`, error);
      throw error;
    }
  }

  getItem(key: string): string | null {
    try {
      return this.storage.getItem(key);
    } catch (error) {
      debugLog.error(`${this.type}: Failed to get item`, error);
      return null;
    }
  }

  removeItem(key: string): void {
    try {
      this.storage.removeItem(key);
    } catch (error) {
      debugLog.error(`${this.type}: Failed to remove item`, error);
    }
  }

  clear(): void {
    try {
      this.storage.clear();
    } catch (error) {
      debugLog.error(`${this.type}: Failed to clear`, error);
    }
  }
}

/**
 * Layered Storage Manager - Implements fallback strategy for iOS compatibility
 */
export class LayeredStorageManager implements StorageInterface {
  private primaryStorage: StorageInterface;
  private fallbackStorages: StorageInterface[] = [];
  readonly type = 'layered';

  constructor() {
    this.initializeStorages();
  }

  private initializeStorages(): void {
    const storages: StorageInterface[] = [];

    // Test localStorage availability
    if (this.testStorage('localStorage')) {
      storages.push(new NativeStorageWrapper(localStorage, 'localStorage'));
    }

    // Test sessionStorage availability
    if (this.testStorage('sessionStorage')) {
      storages.push(new NativeStorageWrapper(sessionStorage, 'sessionStorage'));
    }

    // Test cookie availability
    if (this.testCookies()) {
      storages.push(new CookieStorage());
    }

    // Always add memory storage as ultimate fallback
    storages.push(new MemoryStorage());

    // Set primary and fallback storages
    this.primaryStorage = storages[0] || new MemoryStorage();
    this.fallbackStorages = storages.slice(1);

    debugLog.info('LayeredStorageManager initialized', {
      primary: this.primaryStorage.type,
      fallbacks: this.fallbackStorages.map(s => s.type)
    });
  }

  private testStorage(type: 'localStorage' | 'sessionStorage'): boolean {
    try {
      const storage = window[type];
      const testKey = '__ios_storage_test__';
      storage.setItem(testKey, 'test');
      const result = storage.getItem(testKey);
      storage.removeItem(testKey);
      return result === 'test';
    } catch (error) {
      return false;
    }
  }

  private testCookies(): boolean {
    try {
      const testKey = '__ios_cookie_test__';
      Cookies.set(testKey, 'test');
      const result = Cookies.get(testKey);
      Cookies.remove(testKey);
      return result === 'test';
    } catch (error) {
      return false;
    }
  }

  async setItem(key: string, value: string): Promise<void> {
    // Try primary storage first
    try {
      await this.primaryStorage.setItem(key, value);

      // Also save to first fallback for redundancy
      if (this.fallbackStorages.length > 0) {
        try {
          await this.fallbackStorages[0].setItem(key, value);
        } catch (fallbackError) {
          // Fallback save is optional, don't throw
          debugLog.warn('Fallback storage save failed', fallbackError);
        }
      }
    } catch (error) {
      debugLog.warn(`Primary storage (${this.primaryStorage.type}) failed, trying fallbacks`, error);

      // Try each fallback in order
      for (const storage of this.fallbackStorages) {
        try {
          await storage.setItem(key, value);
          debugLog.info(`Successfully saved to fallback storage: ${storage.type}`);
          return;
        } catch (fallbackError) {
          debugLog.warn(`Fallback storage (${storage.type}) failed`, fallbackError);
        }
      }

      throw new Error('All storage methods failed');
    }
  }

  async getItem(key: string): Promise<string | null> {
    // Try primary storage first
    try {
      const value = await this.primaryStorage.getItem(key);
      if (value !== null) return value;
    } catch (error) {
      debugLog.warn(`Primary storage (${this.primaryStorage.type}) read failed`, error);
    }

    // Try fallbacks
    for (const storage of this.fallbackStorages) {
      try {
        const value = await storage.getItem(key);
        if (value !== null) {
          debugLog.info(`Retrieved from fallback storage: ${storage.type}`);
          return value;
        }
      } catch (error) {
        debugLog.warn(`Fallback storage (${storage.type}) read failed`, error);
      }
    }

    return null;
  }

  async removeItem(key: string): Promise<void> {
    // Remove from all storages
    const promises = [this.primaryStorage, ...this.fallbackStorages].map(async storage => {
      try {
        await storage.removeItem(key);
      } catch (error) {
        debugLog.warn(`Failed to remove from ${storage.type}`, error);
      }
    });

    await Promise.all(promises);
  }

  async clear(): Promise<void> {
    // Clear all storages
    const promises = [this.primaryStorage, ...this.fallbackStorages].map(async storage => {
      try {
        await storage.clear();
      } catch (error) {
        debugLog.warn(`Failed to clear ${storage.type}`, error);
      }
    });

    await Promise.all(promises);
  }

  // Get current storage status for debugging
  getStorageStatus(): {
    primary: string;
    fallbacks: string[];
    available: string[];
  } {
    return {
      primary: this.primaryStorage.type,
      fallbacks: this.fallbackStorages.map(s => s.type),
      available: [this.primaryStorage, ...this.fallbackStorages].map(s => s.type)
    };
  }
}

/**
 * Storage type detection based on data sensitivity
 */
export type DataSensitivity = 'sensitive' | 'preference' | 'cache';

export function getStorageForDataType(dataType: DataSensitivity): StorageInterface {
  switch (dataType) {
    case 'sensitive':
      // Never persist sensitive data - memory only
      return new MemoryStorage();

    case 'preference':
      // Use best available storage
      return new LayeredStorageManager();

    case 'cache':
      // Use whatever is available, no guarantees
      return new LayeredStorageManager();

    default:
      return new MemoryStorage();
  }
}

/**
 * iOS-optimized storage hook for React components
 */
export function useIOSStorage<T>(
  key: string, 
  initialValue: T,
  sensitivity: DataSensitivity = 'preference'
): [T, (value: T) => Promise<void>, () => Promise<void>] {
  const storage = getStorageForDataType(sensitivity);

  const [storedValue, setStoredValue] = React.useState<T>(() => {
    try {
      const item = storage.getItem(key);
      if (item && typeof item === 'string') {
        return JSON.parse(item);
      }
    } catch (error) {
      debugLog.error('Failed to load stored value', error);
    }
    return initialValue;
  });

  const setValue = async (value: T) => {
    try {
      setStoredValue(value);
      await storage.setItem(key, JSON.stringify(value));
    } catch (error) {
      debugLog.error('Failed to save value', error);
      throw error;
    }
  };

  const removeValue = async () => {
    try {
      setStoredValue(initialValue);
      await storage.removeItem(key);
    } catch (error) {
      debugLog.error('Failed to remove value', error);
      throw error;
    }
  };

  return [storedValue, setValue, removeValue];
}

// Export singleton instance for global use
export const iosStorage = new LayeredStorageManager();

// Also need React import for the hook
import React from 'react';

