      /^saveDialogShown$/,
      /^redirectToResults$/,
      /^importanceSkipped$/
    ];

    const keysToRemove: string[] = [];
    let spaceToFree = 0;

    // Scan all localStorage keys
    for (let i = localStorage.length - 1; i >= 0; i--) {
      const key = localStorage.key(i);
      if (!key) continue;

      // Check if this key should be preserved
      const shouldPreserve = preservePatterns.some(pattern => pattern.test(key));
      if (shouldPreserve) {
        debugLog.info(`Preserving application data: ${key}`);
        continue;
      }

      // Check if this key matches development patterns
      const isDevelopmentData = developmentPatterns.some(pattern => pattern.test(key));

      if (isDevelopmentData) {
        const item = localStorage.getItem(key);
        const itemSize = (key.length + (item?.length || 0)) * 2;
        keysToRemove.push(key);
        spaceToFree += itemSize;
      }
    }

    // Remove identified development data
    keysToRemove.forEach(key => {
      try {
        localStorage.removeItem(key);
        result.itemsRemoved++;
        debugLog.info(`Removed development data: ${key}`);
      } catch (error) {
        result.errors.push(`Failed to remove ${key}: ${error}`);
        debugLog.error(`Failed to remove ${key}`, error);
      }
    });

    result.spaceFreed = spaceToFree;
    result.success = result.errors.length === 0;

    debugLog.info('Development data cleanup completed', {
      itemsRemoved: result.itemsRemoved,
      spaceFreed: result.spaceFreed,
      errors: result.errors.length
    });

    return result;
  } catch (error) {
    result.errors.push(`Cleanup failed: ${error}`);
    debugLog.error('Development data cleanup failed', error);
    return result;
  }
}

/**
 * Emergency localStorage cleanup for critical quota issues
 * This is more aggressive and should only be used when Firestore fails to initialize
 */
export function emergencyStorageCleanup(): CleanupResult {
  const result: CleanupResult = {
    success: false,
    itemsRemoved: 0,
    spaceFreed: 0,
    errors: []
  };

  try {
    debugLog.warn('Performing emergency localStorage cleanup due to quota issues');

    // First try development cleanup
    const devCleanup = cleanupDevelopmentData();
    result.itemsRemoved += devCleanup.itemsRemoved;
    result.spaceFreed += devCleanup.spaceFreed;
    result.errors.push(...devCleanup.errors);

    // Check if we still have quota issues
    const quotaCheck = checkStorageQuota();
    if (!quotaCheck.hasQuotaIssue) {
      result.success = true;
      debugLog.info('Emergency cleanup successful, quota issue resolved');
      return result;
    }

    // If still having issues, remove older application data
    const oldDataPatterns = [
      /^backup_/,            // Old backups
      /_backup$/,            // Backup suffixed
      /^archive_/,           // Archived data
      /_old$/,               // Old data
      /^old_/,               // Old prefixed
    ];

    const additionalKeysToRemove: string[] = [];
    let additionalSpaceToFree = 0;

    for (let i = localStorage.length - 1; i >= 0; i--) {
      const key = localStorage.key(i);
      if (!key) continue;

      const isOldData = oldDataPatterns.some(pattern => pattern.test(key));
      if (isOldData) {
        const item = localStorage.getItem(key);
        const itemSize = (key.length + (item?.length || 0)) * 2;
        additionalKeysToRemove.push(key);
        additionalSpaceToFree += itemSize;
      }
    }

    // Remove additional old data
    additionalKeysToRemove.forEach(key => {
      try {
        localStorage.removeItem(key);
        result.itemsRemoved++;
        debugLog.warn(`Emergency removal of old data: ${key}`);
      } catch (error) {
        result.errors.push(`Failed to remove ${key}: ${error}`);
      }
    });

    result.spaceFreed += additionalSpaceToFree;
    result.success = result.errors.length === 0;

    debugLog.warn('Emergency cleanup completed', {
      totalItemsRemoved: result.itemsRemoved,
      totalSpaceFreed: result.spaceFreed,
      errors: result.errors.length
    });

    return result;
  } catch (error) {
    result.errors.push(`Emergency cleanup failed: ${error}`);
    debugLog.error('Emergency storage cleanup failed', error);
    return result;
  }
}

/**
 * Check if localStorage has enough space for a specific operation
 * @param estimatedSize Estimated size needed in bytes
 * @returns Whether the operation is likely to succeed
 */
export function hasStorageSpaceFor(estimatedSize: number): boolean {
  try {
    const quota = checkStorageQuota();

    if (!quota.available) {
      return false;
    }

    // Leave some buffer space (10% or 1KB, whichever is larger)
    const bufferSize = Math.max(quota.total * 0.1, 1024);
    return quota.remaining > (estimatedSize + bufferSize);
  } catch (error) {
    debugLog.error('Failed to check storage space', error);
    return false;
  }
}

/**
 * Prepare localStorage for Firestore operations
 * This should be called before initializing Firestore
 */
export async function prepareStorageForFirestore(): Promise<boolean> {
  try {
    debugLog.info('Preparing localStorage for Firestore operations');

    // Check current quota status
    const initialQuota = checkStorageQuota();
    debugLog.info('Initial storage quota', initialQuota);

    // If we have quota issues, perform cleanup
    if (initialQuota.hasQuotaIssue) {
      debugLog.warn('Storage quota issue detected, performing cleanup');

      // Try development cleanup first
      const cleanup = cleanupDevelopmentData();
      debugLog.info('Development cleanup result', cleanup);

      // Check quota again
      const postCleanupQuota = checkStorageQuota();

      // If still having issues, try emergency cleanup
      if (postCleanupQuota.hasQuotaIssue) {
        debugLog.warn('Still having quota issues, performing emergency cleanup');
        const emergencyCleanup = emergencyStorageCleanup();
        debugLog.warn('Emergency cleanup result', emergencyCleanup);
      }
    }

    // Final quota check
    const finalQuota = checkStorageQuota();
    debugLog.info('Final storage quota after preparation', finalQuota);

    // Ensure we have space for Firestore offline cache (estimate 1MB minimum)
    const hasSpaceForFirestore = hasStorageSpaceFor(1048576); // 1MB

    if (!hasSpaceForFirestore) {
      debugLog.error('Insufficient storage space for Firestore operations');
      return false;
    }

    debugLog.info('Storage successfully prepared for Firestore');
    return true;
  } catch (error) {
    debugLog.error('Failed to prepare storage for Firestore', error);
    return false;
  }
}

/**
 * Monitor storage usage and provide warnings
 */
export function startStorageMonitoring(): () => void {
  let interval: NodeJS.Timeout;

  try {
    // Check storage every 30 seconds
    interval = setInterval(() => {
      const quota = checkStorageQuota();

      if (quota.percentUsed > 80) {
        debugLog.warn('Storage usage high', {
          percentUsed: quota.percentUsed.toFixed(2),
          used: quota.used,
          remaining: quota.remaining
        });
      }

      if (quota.hasQuotaIssue) {
        debugLog.error('Storage quota issue detected during monitoring', quota);
      }
    }, 30000);
  } catch (error) {
    debugLog.error('Failed to start storage monitoring', error);
  }

  // Return cleanup function
  return () => {
    if (interval) {
      clearInterval(interval);
    }
  };
}

/**
 * Get detailed storage usage report for debugging
 */
export function getStorageUsageReport(): {
  quota: StorageQuotaInfo;
  items: Array<{ key: string; size: number; type: string }>;
  summary: {
    totalItems: number;
    largestItem: { key: string; size: number } | null;
    applicationData: number;
    developmentData: number;
    unknownData: number;
  };
} {
  const quota = checkStorageQuota();
  const items: Array<{ key: string; size: number; type: string }> = [];
  let applicationData = 0;
  let developmentData = 0;
  let unknownData = 0;
  let largestItem: { key: string; size: number } | null = null;

  try {
    // Analyze each localStorage item
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (!key) continue;

      const item = localStorage.getItem(key);
      const size = (key.length + (item?.length || 0)) * 2;

      // Categorize the item
      let type = 'unknown';
      if (/^(importanceResults|jobSatisfactionResults|satisfactionResponses|saveDialogShown|redirectToResults|importanceSkipped)$/.test(key)) {
        type = 'application';
        applicationData += size;
      } else if (/^(firebase_|firestore_|__test__|debug_|dev_|temp_|cache_)/.test(key)) {
        type = 'development';
        developmentData += size;
      } else {
        type = 'unknown';
        unknownData += size;
      }

      items.push({ key, size, type });

      // Track largest item
      if (!largestItem || size > largestItem.size) {
        largestItem = { key, size };
      }
    }

    // Sort items by size (descending)
    items.sort((a, b) => b.size - a.size);
  } catch (error) {
    debugLog.error('Failed to generate storage usage report', error);
  }

  return {
    quota,
    items,
    summary: {
      totalItems: items.length,
      largestItem,
      applicationData,
      developmentData,
      unknownData
    }
  };
}

