// Firebase Authentication Service
// This file provides functions for user authentication

import { 
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  updateProfile,
  sendPasswordResetEmail,
  User,
  UserCredential,
  onAuthStateChanged
} from "firebase/auth";
import { auth, firestore } from "./config";
import { doc, setDoc, getDoc, serverTimestamp } from "firebase/firestore";

/**
 * Register a new user with email and password
 * @param email User's email
 * @param password User's password
 * @returns Promise resolving to UserCredential
 */
export const registerUser = async (email: string, password: string): Promise<UserCredential> => {
  try {
    const userCredential = await createUserWithEmailAndPassword(auth, email, password);

    // Create user document in Firestore
    await createUserDocument(userCredential.user);

    return userCredential;
  } catch (error) {
    console.error("Error registering user:", error);
    throw error;
  }
};

/**
 * Create a user document in Firestore
 * @param user Firebase User object
 */
export const createUserDocument = async (user: User): Promise<void> => {
  try {
    const userRef = doc(firestore, "users", user.uid);
    const userSnap = await getDoc(userRef);

    // Only create document if it doesn't exist
    if (!userSnap.exists()) {
      await setDoc(userRef, {
        email: user.email,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
        displayName: user.displayName || null,
        photoURL: user.photoURL || null,
      });
    }
  } catch (error) {
    console.error("Error creating user document:", error);
    throw error;
  }
};

/**
 * Sign in a user with email and password
 * @param email User's email
 * @param password User's password
 * @returns Promise resolving to UserCredential
 */
export const loginUser = async (email: string, password: string): Promise<UserCredential> => {
  try {
    return await signInWithEmailAndPassword(auth, email, password);
  } catch (error) {
    console.error("Error logging in user:", error);
    throw error;
  }
};

/**
 * Sign out the current user
 */
export const logoutUser = async (): Promise<void> => {
  try {
    await signOut(auth);
  } catch (error) {
    console.error("Error signing out user:", error);
    throw error;
  }
};

/**
 * Update user profile information
 * @param displayName User's display name
 * @param photoURL User's photo URL
 */
export const updateUserProfile = async (displayName?: string, photoURL?: string): Promise<void> => {
  try {
    const user = auth.currentUser;
    if (!user) throw new Error("No user is signed in");

    const updateData: { displayName?: string; photoURL?: string } = {};
    if (displayName !== undefined) updateData.displayName = displayName;
    if (photoURL !== undefined) updateData.photoURL = photoURL;

    // Update the Firebase Auth user profile
    await updateProfile(user, updateData);

    // Force a refresh to ensure we get the latest user data
    await user.reload();

    // Update user document in Firestore
    const userRef = doc(firestore, "users", user.uid);
    await setDoc(userRef, {
      ...updateData,
      updatedAt: serverTimestamp(),
    }, { merge: true });

    console.log("Profile updated successfully:", { displayName, photoURL });

  } catch (error) {
    console.error("Error updating user profile:", error);
    throw error;
  }
};

/**
 * Send password reset email
 * @param email User's email
 */
export const resetPassword = async (email: string): Promise<void> => {
  try {
    await sendPasswordResetEmail(auth, email);
  } catch (error) {
    console.error("Error sending password reset email:", error);
    throw error;
  }
};

/**
 * Get the current authenticated user
 * @returns Current user or null if not authenticated
 */
export const getCurrentUser = (): User | null => {
  return auth.currentUser;
};

/**
 * Subscribe to auth state changes
 * @param callback Function to call when auth state changes
 * @returns Unsubscribe function
 */
export const subscribeToAuthChanges = (callback: (user: User | null) => void) => {
  return onAuthStateChanged(auth, callback);
};

