/**
 * Create a user document in Firestore with storage quota management
 * @param user Firebase User object
 */
export const createUserDocument = async (user: User): Promise<void> => {
  try {
    // Check storage quota before attempting Firestore operations
    const quota = checkStorageQuota();
    console.log('Storage quota check before user document creation:', quota);

    if (quota.hasQuotaIssue) {
      console.warn('Storage quota issue detected before creating user document');

      // Attempt emergency cleanup
      const cleanupResult = await emergencyStorageCleanup();
      console.log('Emergency cleanup result:', cleanupResult);

      // Recheck quota after cleanup
      const postCleanupQuota = checkStorageQuota();
      if (postCleanupQuota.hasQuotaIssue) {
        console.error('Storage quota still critical after cleanup. User document creation may fail.');
        // Continue anyway - Firestore might work in online-only mode
      }
    }

    // Estimate storage needed for user document (rough calculation)
    const estimatedDocSize = JSON.stringify({
      email: user.email,
      createdAt: new Date(),
      updatedAt: new Date(),
      displayName: user.displayName || null,
      photoURL: user.photoURL || null,
    }).length * 2; // UTF-16 encoding

    if (!hasStorageSpaceFor(estimatedDocSize)) {
      console.warn('Insufficient storage space for user document, but proceeding with online-only mode');
    }

    const userRef = doc(firestore, "users", user.uid);
    const userSnap = await getDoc(userRef);

    // Only create document if it doesn't exist
    if (!userSnap.exists()) {
      await setDoc(userRef, {
        email: user.email,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
        displayName: user.displayName || null,
        photoURL: user.photoURL || null,
      });
      console.log('User document created successfully for:', user.email);
    } else {
      console.log('User document already exists for:', user.email);
    }
  } catch (error) {
    console.error("Error creating user document:", error);

    // Check if this is a quota-related error
    if (error instanceof Error && 
        (error.message.includes('QuotaExceededError') || 
         error.message.includes('quota') || 
         error.message.includes('storage'))) {

      console.error('Storage quota error detected during user document creation');

      // Try emergency cleanup one more time
      try {
        const emergencyCleanup = await emergencyStorageCleanup();
        console.log('Emergency cleanup after error:', emergencyCleanup);

        // Provide user-friendly error message
        throw new Error('Storage space is full. Please clear browser data and try again. Contact support if the issue persists.');
      } catch (cleanupError) {
        console.error('Emergency cleanup failed:', cleanupError);
        throw new Error('Critical storage issue. Please clear browser data, restart your browser, and try again.');
      }
    }

    // For other types of errors, provide specific guidance
    if (error instanceof Error && error.message.includes('FIRESTORE INTERNAL ASSERTION FAILED')) {
      throw new Error('Database connection issue. Please check your internet connection and try again.');
    }

    // Re-throw original error with additional context
    throw error;
  }
};

