/**
 * Development Storage Utilities
 * 
 * This module provides utilities for developers to manage localStorage during development.
 * It includes functions to clear, analyze, and debug storage issues.
 * 
 * Usage in browser console:
 * - window.clearDevelopmentStorage() - Clear development data
 * - window.emergencyStorageCleanup() - Emergency cleanup
 * - window.getStorageReport() - Get detailed storage report
 * - window.clearAllStorage() - Clear all localStorage (DANGER)
 */

import { 
  checkStorageQuota, 
  cleanupDevelopmentData, 
  emergencyStorageCleanup, 
  getStorageUsageReport,
  prepareStorageForFirestore 
} from './storageQuotaManager';
import { debugLog } from './debugLogger';

/**
 * Clear development and testing data from localStorage
 * Safe for production use - preserves application data
 */
export const clearDevelopmentStorage = async (): Promise<void> => {
  try {
    console.group('🧹 Development Storage Cleanup');

    const beforeQuota = checkStorageQuota();
    console.log('Storage before cleanup:', beforeQuota);

    const cleanup = cleanupDevelopmentData();
    console.log('Cleanup result:', cleanup);

    const afterQuota = checkStorageQuota();
    console.log('Storage after cleanup:', afterQuota);

    const improvement = beforeQuota.percentUsed - afterQuota.percentUsed;
    console.log(`✅ Storage usage improved by ${improvement.toFixed(2)}%`);

    console.groupEnd();
  } catch (error) {
    console.error('❌ Development storage cleanup failed:', error);
  }
};

/**
 * Perform emergency storage cleanup
 * More aggressive cleanup for critical quota issues
 */
export const performEmergencyCleanup = async (): Promise<void> => {
  try {
    console.group('🚨 Emergency Storage Cleanup');
    console.warn('Performing aggressive storage cleanup...');

    const beforeQuota = checkStorageQuota();
    console.log('Storage before emergency cleanup:', beforeQuota);

    const emergency = await emergencyStorageCleanup();
    console.log('Emergency cleanup result:', emergency);

    const afterQuota = checkStorageQuota();
    console.log('Storage after emergency cleanup:', afterQuota);

    const improvement = beforeQuota.percentUsed - afterQuota.percentUsed;
    console.log(`✅ Storage usage improved by ${improvement.toFixed(2)}%`);

    console.groupEnd();
  } catch (error) {
    console.error('❌ Emergency storage cleanup failed:', error);
  }
};

/**
 * Get detailed storage usage report
 * Useful for debugging storage issues
 */
export const getDetailedStorageReport = (): void => {
  try {
    console.group('📊 Detailed Storage Report');

    const report = getStorageUsageReport();

    console.log('Storage Quota:', report.quota);
    console.log('Storage Summary:', report.summary);

    console.log('📁 Items by Size (Top 20):');
    console.table(report.items.slice(0, 20).map(item => ({
      Key: item.key,
      'Size (bytes)': item.size.toLocaleString(),
      'Size (KB)': (item.size / 1024).toFixed(2),
      Type: item.type
    })));

    console.log('📈 Storage Breakdown:');
    const breakdown = {
      'Application Data': `${(report.summary.applicationData / 1024).toFixed(2)} KB`,
      'Development Data': `${(report.summary.developmentData / 1024).toFixed(2)} KB`,
      'Unknown Data': `${(report.summary.unknownData / 1024).toFixed(2)} KB`,
      'Total Items': report.summary.totalItems
    };
    console.table(breakdown);

    if (report.summary.largestItem) {
      console.log('🏆 Largest Item:', {
        key: report.summary.largestItem.key,
        size: `${(report.summary.largestItem.size / 1024).toFixed(2)} KB`
      });
    }

    // Recommendations
    console.log('💡 Recommendations:');
    if (report.quota.percentUsed > 80) {
      console.warn('⚠️  Storage usage is high. Consider running cleanup.');
    }

    if (report.summary.developmentData > 1048576) { // 1MB
      console.warn('⚠️  Large amount of development data found. Run clearDevelopmentStorage()');
    }

    if (report.quota.hasQuotaIssue) {
      console.error('🚨 Critical storage quota issue. Run performEmergencyCleanup()');
    }

    console.groupEnd();
  } catch (error) {
    console.error('❌ Failed to generate storage report:', error);
  }
};

/**
 * Clear ALL localStorage data
 * DANGER: This will remove all application data
 * Only use for testing or when absolutely necessary
 */
export const clearAllStorage = (): void => {
  if (confirm('⚠️ DANGER: This will clear ALL localStorage data including application data. Are you sure?')) {
    if (confirm('🚨 FINAL WARNING: This action cannot be undone. All user data will be lost. Continue?')) {
      try {
        const beforeCount = localStorage.length;
        localStorage.clear();
        const afterCount = localStorage.length;

        console.log('🗑️ All localStorage cleared:', {
          itemsRemoved: beforeCount,
          remainingItems: afterCount
        });

        // Also clear other storage types
        if (window.sessionStorage) {
          sessionStorage.clear();
          console.log('🗑️ SessionStorage cleared');
        }

        // Note: We can't clear IndexedDB from here, but localStorage clear should be sufficient

        console.log('✅ Complete storage clear completed. Reload the page to start fresh.');
      } catch (error) {
        console.error('❌ Failed to clear all storage:', error);
      }
    }
  }
};

/**
 * Test localStorage quota limits
 * Useful for understanding browser storage limits
 */
export const testStorageQuota = (): void => {
  try {
    console.group('🧪 Storage Quota Test');

    const initialQuota = checkStorageQuota();
    console.log('Initial quota:', initialQuota);

    // Test with progressively larger data
    const testSizes = [1024, 10240, 102400, 1048576]; // 1KB, 10KB, 100KB, 1MB

    for (const size of testSizes) {
      const testKey = `__quota_test_${size}__`;
      const testData = 'x'.repeat(size);

      try {
        localStorage.setItem(testKey, testData);
        console.log(`✅ Successfully stored ${(size / 1024).toFixed(1)}KB`);
        localStorage.removeItem(testKey);
      } catch (error) {
        console.log(`❌ Failed to store ${(size / 1024).toFixed(1)}KB:`, error);
        break;
      }
    }

    const finalQuota = checkStorageQuota();
    console.log('Final quota:', finalQuota);

    console.groupEnd();
  } catch (error) {
    console.error('❌ Storage quota test failed:', error);
  }
};

/**
 * Prepare storage for Firestore and provide feedback
 */
export const prepareStorageForDevelopment = async (): Promise<void> => {
  try {
    console.group('🔧 Preparing Storage for Development');

    const beforeQuota = checkStorageQuota();
    console.log('Storage before preparation:', beforeQuota);

    const success = await prepareStorageForFirestore();

    const afterQuota = checkStorageQuota();
    console.log('Storage after preparation:', afterQuota);

    if (success) {
      console.log('✅ Storage prepared successfully for Firestore');
    } else {
      console.warn('⚠️ Storage preparation completed with warnings');
    }

    console.groupEnd();
  } catch (error) {
    console.error('❌ Storage preparation failed:', error);
  }
};

/**
 * Monitor localStorage in real-time
 * Shows storage changes as they happen
 */
export const startStorageMonitoring = (): (() => void) => {
  console.log('📊 Starting localStorage monitoring...');

  let previousQuota = checkStorageQuota();
  console.log('Initial storage state:', previousQuota);

  const interval = setInterval(() => {
    const currentQuota = checkStorageQuota();

    // Only log if there are significant changes
    if (Math.abs(currentQuota.percentUsed - previousQuota.percentUsed) > 1) {
      console.log('📈 Storage change detected:', {
        percentUsed: `${currentQuota.percentUsed.toFixed(2)}%`,
        used: `${(currentQuota.used / 1024).toFixed(2)}KB`,
        remaining: `${(currentQuota.remaining / 1024).toFixed(2)}KB`
      });

      if (currentQuota.hasQuotaIssue && !previousQuota.hasQuotaIssue) {
        console.warn('🚨 Storage quota issue detected!');
      }
    }

    previousQuota = currentQuota;
  }, 5000); // Check every 5 seconds

  // Return cleanup function
  return () => {
    clearInterval(interval);
    console.log('📊 Storage monitoring stopped');
  };
};

// Development-only: Expose utilities to window object for console access
if (import.meta.env.DEV) {
  // Type declarations for window object
  declare global {
    interface Window {
      clearDevelopmentStorage: () => Promise<void>;
      emergencyStorageCleanup: () => Promise<void>;
      getStorageReport: () => void;
      clearAllStorage: () => void;
      testStorageQuota: () => void;
      prepareStorageForDev: () => Promise<void>;
      startStorageMonitoring: () => (() => void);
    }
  }

  // Expose functions to window object
  window.clearDevelopmentStorage = clearDevelopmentStorage;
  window.emergencyStorageCleanup = performEmergencyCleanup;
  window.getStorageReport = getDetailedStorageReport;
  window.clearAllStorage = clearAllStorage;
  window.testStorageQuota = testStorageQuota;
  window.prepareStorageForDev = prepareStorageForDevelopment;
  window.startStorageMonitoring = startStorageMonitoring;

  // Log available functions
  console.log(`
🛠️ Development Storage Utilities Available:

Basic Operations:
• clearDevelopmentStorage() - Safe cleanup of dev data
• getStorageReport() - Detailed storage analysis

Emergency Operations:
• emergencyStorageCleanup() - Aggressive cleanup
• clearAllStorage() - ⚠️ DANGER: Clears everything

Testing & Monitoring:
• testStorageQuota() - Test storage limits
• prepareStorageForDev() - Prepare for Firestore
• startStorageMonitoring() - Real-time monitoring

💡 Recommended: Run getStorageReport() first to understand current usage
  `);
}

