   200→
   201→    setIsSubmitting(true);
   202→    
   203→    // Add timeout to prevent infinite processing
   204→    const OVERALL_TIMEOUT = 45000; // 45 seconds max for full assessment
   205→    let timeoutId: NodeJS.Timeout | null = null;
   206→    
   207→    try {
   208→      // Set overall timeout to prevent hanging
   209→      const overallTimeout = new Promise((_, reject) => {
