   291→          importanceSkipped: wasImportanceSkipped,
   292→          jobSatisfactionResults: results,
   293→          status: 'completed' as const,
   294→          completedAt: new Date(),
   295→          step: 'completed'
   296→        };
   297→        
   298→        // Save to Firebase (with automatic local fallback) - with shorter timeout
   299→        let firebaseTimeoutId: NodeJS.Timeout | null = null;
   300→        const assessmentId = await Promise.race([
   301→          assessmentService.saveAssessment(assessmentData, userId),
   302→          new Promise<string>((_, reject) => {
   303→            firebaseTimeoutId = setTimeout(() => reject(new Error('Firebase timeout')), 7000);
   304→          })
