   306→          if (firebaseTimeoutId) clearTimeout(firebaseTimeoutId);
   307→        });
   308→        
   309→        // Save assessment ID for results page
   310→        await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   311→        console.log('✅ Successfully saved to Firebase');
   312→      } catch (firebaseError) {
