   314→        // Continue - the data is already saved locally
   315→      }
   316→      
   317→      // Legacy hook call for backward compatibility with timeout
   318→      try {
   319→        await Promise.race([
   320→          saveAssessmentData({
   321→            satisfactionRatings,
   322→            jobSatisfactionResults: results,
