   120→    }
   121→  }, [currentQuestionIndex]);
   122→
   123→  const currentQuestion = currentQuestionIndex >= 0 ? questions[currentQuestionIndex] : null;
   124→  const currentAnswer = currentQuestionIndex >= 0 ? answers[currentQuestionIndex] : null;
   125→
   126→  const handleAnswer = (value: number) => {
   127→    if (currentQuestionIndex >= 0) {
   128→      const newAnswers = [...answers];
