   143→    }
   144→    setCurrentQuestionIndex(0);
   145→  };
   146→
   147→  const goToPrevious = () => {
   148→    if (currentQuestionIndex > 0) {
   149→      setCurrentQuestionIndex(currentQuestionIndex - 1);
   150→    } else if (currentQuestionIndex === 0) {
   151→      setCurrentQuestionIndex(-1); // Go back to intro
