   153→  };
   154→
   155→  const goToNext = async () => {
   156→    if (currentQuestionIndex === -1) {
   157→      await startTest();
   158→      return;
   159→    }
   160→    
   161→    if (!currentAnswer || currentAnswer.rating === null) {
   162→      toast({
   163→        title: "Please rate this aspect",
   164→        description: "Select a rating before moving to the next question",
   165→        variant: "destructive"
   166→      });
   167→      return;
   168→    }
   169→    
   170→    if (currentQuestionIndex < questions.length - 1) {
   171→      setCurrentQuestionIndex(currentQuestionIndex + 1);
   172→    }
   173→  };
   174→
   175→  const handleSubmit = async () => {
   176→    const unansweredIndex = answers.findIndex(answer => answer.rating === null);
   177→    if (unansweredIndex !== -1) {
   178→      setCurrentQuestionIndex(unansweredIndex);
   179→      toast({
   180→        title: "Incomplete Assessment",
   181→        description: `Please rate all aspects. Going to question ${unansweredIndex + 1}`,
   182→        variant: "destructive"
   183→      });
   184→      return;
   185→    }
   186→    
   187→    setIsSubmitting(true);
   188→    
   189→    // Add timeout to prevent infinite processing
   190→    const OVERALL_TIMEOUT = 30000; // 30 seconds max
   191→    let timeoutId: NodeJS.Timeout | null = null;
   192→    
   193→    try {
   194→      // Set overall timeout to prevent hanging
   195→      const overallTimeout = new Promise((_, reject) => {
   196→        timeoutId = setTimeout(() => {
   197→          reject(new Error('Overall submission timeout - process taking too long'));
