   205→      toast({
   206→        title: "Submission Error",
   207→        description: error instanceof Error ? error.message : "An unexpected error occurred. Please try again.",
   208→        variant: "destructive"
   209→      });
   210→    } finally {
   211→      if (timeoutId) clearTimeout(timeoutId);
   212→      setIsSubmitting(false);
   213→    }
   214→  };
   215→  
   216→  const submitLogic = async () => {
   217→    
   218→    const importanceResults = questions.map((q, index) => ({
