   292→          }
   293→          
   294→          toast({
   295→            title: "Results Updated!",
   296→            description: "Your overall score has been recalculated based on your importance ratings.",
   297→          });
   298→        }
   299→        
   300→        navigate('/results');
   301→      } else {
   302→        // Normal flow - importance first, then satisfaction
   303→        // Try Firebase save but don't block on it
   304→        try {
   305→          // Get or create user for saving draft with shorter timeout
   306→          let authTimeoutId: NodeJS.Timeout | null = null;
   307→          const user = await Promise.race([
   308→            authenticationService.ensureAuthenticated(),
   309→            new Promise<null>((_, reject) => {
   310→              authTimeoutId = setTimeout(() => reject(new Error('Auth timeout')), 3000);
   311→            })
   312→          ]).finally(() => {
   313→            if (authTimeoutId) clearTimeout(authTimeoutId);
   314→          });
   315→          const userId = user?.uid || 'anonymous';
   316→          
   317→          // Generate assessment ID for this session
   318→          const assessmentId = `assessment_${Date.now()}_${Math.random().toString(36).substring(2, 11)}`;
   319→          await setCrossDeviceStorageItem('current_assessment_id', assessmentId);
   320→          
   321→          // Save importance data as draft to Firebase with shorter timeout
