   334→            })
   335→          ]).finally(() => {
   336→            if (firebaseTimeoutId) clearTimeout(firebaseTimeoutId);
   337→          });
   338→          console.log('✅ Successfully saved importance to Firebase');
   339→        } catch (firebaseError) {
