    33→interface JobSatisfactionResults {
    34→  overallScore?: number;
    35→  totalScore?: number;
    36→  categoryScores?: AllCategoryScores;
    37→  categories?: any[];
    38→}
    39→
    40→// Enhanced helper function to normalize assessment data structure for iOS compatibility
    41→const normalizeAssessmentData = (data: any): JobSatisfactionResults | null => {
    42→  if (!data) {
    43→    console.warn('normalizeAssessmentData: No data provided');
    44→    return null;
    45→  }
    46→  
    47→  debugLog.info('normalizeAssessmentData: Starting normalization', { 
    48→    dataType: typeof data,
    49→    dataKeys: Object.keys(data)
    50→  });
    51→  
    52→  // Use the utility function to unwrap data if it's wrapped
    53→  // getCrossDeviceStorageItemUnwrapped already handles all the unwrapping logic
    54→  const actualData = data;
    55→  
    56→  debugLog.info('normalizeAssessmentData: Processing data', { 
    57→    actualDataType: typeof actualData,
    58→    actualDataKeys: actualData ? Object.keys(actualData) : []
    59→  });
    60→  
    61→  // Handle multiple score format variations with better error handling
    62→  let score = undefined;
    63→  const scoreFields = ['overallScore', 'totalScore', 'score', 'satisfactionScore'];
    64→  
    65→  for (const field of scoreFields) {
    66→    if (actualData[field] !== undefined && actualData[field] !== null) {
    67→      score = actualData[field];
    68→      debugLog.info(`normalizeAssessmentData: Found score in ${field}:`, score);
    69→      break;
    70→    }
    71→  }
    72→  
    73→  // Get categories with enhanced fallback handling
    74→  let categories = actualData.categoryScores || actualData.categories || actualData.categoryData;
    75→  
    76→  debugLog.info('normalizeAssessmentData: Raw categories data', { 
    77→    categories,
    78→    categoriesType: typeof categories,
    79→    categoriesKeys: categories ? Object.keys(categories) : []
    80→  });
    81→  
    82→  // Robust category processing with iOS-specific fixes
    83→  if (categories && typeof categories === 'object') {
    84→    const normalizedCategories: any = {};
    85→    
    86→    Object.entries(categories).forEach(([categoryId, categoryData]) => {
    87→      const normalizedId = normalizeCategoryId(categoryId);
    88→      
    89→      // Handle different category data formats
    90→      let processedCategoryData;
    91→      if (typeof categoryData === 'number') {
    92→        // Simple number format
    93→        processedCategoryData = { score: categoryData };
    94→      } else if (categoryData && typeof categoryData === 'object') {
    95→        // Object format - ensure it has a score property
    96→        const categoryObj = categoryData as any;
    97→        processedCategoryData = {
    98→          score: categoryObj.score !== undefined ? categoryObj.score : 0,
    99→          ...categoryObj
   100→        };
   101→      } else {
   102→        // Fallback for unexpected formats
   103→        console.warn(`normalizeAssessmentData: Unexpected category data format for ${categoryId}:`, categoryData);
   104→        processedCategoryData = { score: 0 };
   105→      }
   106→      
   107→      normalizedCategories[normalizedId] = processedCategoryData;
   108→    });
   109→    
   110→    categories = normalizedCategories;
   111→    debugLog.info('normalizeAssessmentData: Normalized categories', categories);
   112→  } else if (categories) {
   113→    console.warn('normalizeAssessmentData: Categories data is not an object:', categories);
   114→    categories = {};
   115→  }
   116→  
   117→  // Enhanced score validation
   118→  if (score === undefined || score === null || isNaN(score)) {
   119→    console.error('normalizeAssessmentData: No valid score found in assessment data', { 
   120→      actualData, 
   121→      checkedFields: scoreFields,
   122→      foundScore: score 
   123→    });
   124→    return null;
   125→  }
   126→  
   127→  const result = {
   128→    overallScore: score,
   129→    totalScore: score,
   130→    categoryScores: categories,
   131→    categories: categories
   132→  };
   133→  
   134→  debugLog.info('normalizeAssessmentData: Final normalized result', result);
   135→  
   136→  return result;
   137→};
