   139→// Helper function to get the score value, handling both overallScore and totalScore
   140→const getScoreValue = (results: JobSatisfactionResults | null): number => {
   141→  if (!results) return 0;
   142→  return results.overallScore !== undefined ? results.overallScore : results.totalScore || 0;
   143→};
