import { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Timestamp } from 'firebase/firestore';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Header } from '@/components/Header';
import { BackgroundWrapper } from '@/components/BackgroundWrapper';
import { DetailedResults } from '@/components/DetailedResults';
import { JobValueCompass } from '@/components/results/JobValueCompass';
import { ScoreDisplay, transformScoreForDisplay, getScoreBackgroundColor } from '@/components/ScoreDisplay';
import { ScoreExplainer } from '@/components/ScoreExplainer';
import { CoachingInsights } from '@/components/CoachingInsights';
import { AllCategoryScores } from '@/components/AssessmentForm';
import { useAuth } from '@/context';
import { useAssessmentHistory } from '@/hooks/useAssessmentHistory';
import { useBenchmarks } from '@/hooks/useBenchmarks';
import { INDUSTRY_CATEGORIES, AssessmentData } from '@/models/AssessmentModels';
import { STORAGE_KEYS } from '@/utils/storageUtils';
import { getCrossDeviceStorageItem, setCrossDeviceStorageItem, getCrossDeviceStorageItemUnwrapped } from '@/utils/crossDeviceStorage';
import { getNavigationState, clearNavigationState, isIOSDevice, normalizeImportanceData } from '@/utils/iOSNavigationFix';
import { debugLog } from '@/utils/debugLogger';
import { ChevronLeft, Save, TrendingUp, Building2, Briefcase, BarChart3, Target, Award } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { assessmentService } from '@/services/assessmentService';
import { authenticationService } from '@/services/authenticationService';
import { getCategoryDisplayName, normalizeCategoryId } from '@/utils/categoryDefinitions';

interface JobSatisfactionResults {
  overallScore?: number;
  totalScore?: number;
  categoryScores?: AllCategoryScores;
  categories?: any[];
}

// Enhanced helper function to normalize assessment data structure for iOS compatibility
const normalizeAssessmentData = (data: any): JobSatisfactionResults | null => {
  if (!data) {
    console.warn('normalizeAssessmentData: No data provided');
    return null;
  }

  debugLog.info('normalizeAssessmentData: Starting normalization', { 
    dataType: typeof data,
    dataKeys: Object.keys(data)
  });

  // Use the utility function to unwrap data if it's wrapped
  // getCrossDeviceStorageItemUnwrapped already handles all the unwrapping logic
  const actualData = data;

  debugLog.info('normalizeAssessmentData: Processing data', { 
    actualDataType: typeof actualData,
    actualDataKeys: actualData ? Object.keys(actualData) : []
  });

  // Handle multiple score format variations with better error handling
  let score = undefined;
  const scoreFields = ['overallScore', 'totalScore', 'score', 'satisfactionScore'];

  for (const field of scoreFields) {
    if (actualData[field] !== undefined && actualData[field] !== null) {
      score = actualData[field];
      debugLog.info(`normalizeAssessmentData: Found score in ${field}:`, score);
      break;
    }
  }

  // Get categories with enhanced fallback handling
  let categories = actualData.categoryScores || actualData.categories || actualData.categoryData;

  debugLog.info('normalizeAssessmentData: Raw categories data', { 
    categories,
    categoriesType: typeof categories,
    categoriesKeys: categories ? Object.keys(categories) : []
  });

  // Robust category processing with iOS-specific fixes
  if (categories && typeof categories === 'object') {
    const normalizedCategories: any = {};

    Object.entries(categories).forEach(([categoryId, categoryData]) => {
      const normalizedId = normalizeCategoryId(categoryId);

      // Handle different category data formats
      let processedCategoryData;
      if (typeof categoryData === 'number') {
        // Simple number format
        processedCategoryData = { score: categoryData };
      } else if (categoryData && typeof categoryData === 'object') {
        // Object format - ensure it has a score property
        const categoryObj = categoryData as any;
        processedCategoryData = {
          score: categoryObj.score !== undefined ? categoryObj.score : 0,
          ...categoryObj
        };
      } else {
        // Fallback for unexpected formats
        console.warn(`normalizeAssessmentData: Unexpected category data format for ${categoryId}:`, categoryData);
        processedCategoryData = { score: 0 };
      }

      normalizedCategories[normalizedId] = processedCategoryData;
    });

    categories = normalizedCategories;
    debugLog.info('normalizeAssessmentData: Normalized categories', categories);
  } else if (categories) {
    console.warn('normalizeAssessmentData: Categories data is not an object:', categories);
    categories = {};
  }

  // Enhanced score validation
  if (score === undefined || score === null || isNaN(score)) {
    console.error('normalizeAssessmentData: No valid score found in assessment data', { 
      actualData, 
      checkedFields: scoreFields,
      foundScore: score 
    });
    return null;
  }

  const result = {
    overallScore: score,
    totalScore: score,
    categoryScores: categories,
    categories: categories
  };

  debugLog.info('normalizeAssessmentData: Final normalized result', result);

  return result;
};

// Helper function to get the score value, handling both overallScore and totalScore
const getScoreValue = (results: JobSatisfactionResults | null): number => {
  if (!results) return 0;
  return results.overallScore !== undefined ? results.overallScore : results.totalScore || 0;
};

const ResultsEnhanced = () => {
  const [results, setResults] = useState<JobSatisfactionResults | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isSaving, setIsSaving] = useState(false);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [industryComparison, setIndustryComparison] = useState<any>(null);

  // Form fields for saving
  const [companyName, setCompanyName] = useState('');
  const [industry, setIndustry] = useState<string>('');
  const [jobTitle, setJobTitle] = useState('');
  const [department, setDepartment] = useState('');

  const navigate = useNavigate();
  const location = useLocation();
  const { currentUser } = useAuth();
  const { saveAssessment } = useAssessmentHistory();
  const { compareToIndustry } = useBenchmarks();
  const { toast } = useToast();

  useEffect(() => {
    const loadResults = async () => {
      setIsLoading(true);
      setError(null);

      try {
        // Get results from location state or storage
        let rawAssessmentData: any = null;
        let assessmentFromHistory: AssessmentData | null = null;

        // Check if we have assessment in location state (from ProfileWithHistory)
        if (location.state?.assessment) {
          assessmentFromHistory = location.state.assessment;
          rawAssessmentData = assessmentFromHistory.jobSatisfactionResults;
        } else {
          // Firebase-first approach: Get user and fetch from Firebase
          const user = await authenticationService.ensureAuthenticated();
          const userId = user?.uid;

          // Check for assessment ID
          const assessmentId = await getCrossDeviceStorageItem<string>('current_assessment_id', null);

          if (assessmentId && userId) {
            // Try to get specific assessment from Firebase
            const firebaseAssessment = await assessmentService.getAssessment(assessmentId, userId);
            if (firebaseAssessment) {
              debugLog.info('Loaded assessment from Firebase', { assessmentId });
              rawAssessmentData = firebaseAssessment.jobSatisfactionResults;
              assessmentFromHistory = firebaseAssessment;
            }
          }

          // If no specific assessment, get most recent results
          if (!rawAssessmentData && userId) {
            const recentResults = await assessmentService.getMostRecentResults(userId);
            if (recentResults) {
              debugLog.info('Loaded most recent results from Firebase');
              rawAssessmentData = recentResults.jobSatisfactionResults;
              assessmentFromHistory = recentResults;
            }
          }

          // Fallback to iOS navigation state
          if (!rawAssessmentData && isIOSDevice()) {
            const savedState = await getNavigationState();
            if (savedState) {
              assessmentFromHistory = savedState;
              rawAssessmentData = savedState.jobSatisfactionResults;
              await clearNavigationState();
              debugLog.info('Loaded from iOS navigation state');
            }
          }

          // Final fallback to local storage (for backward compatibility)
          if (!rawAssessmentData) {
            // Use unwrapped version to get the actual data without metadata wrapper
            rawAssessmentData = await getCrossDeviceStorageItemUnwrapped<any>(
              STORAGE_KEYS.JOB_SATISFACTION_RESULTS, 
              null
            );

            if (rawAssessmentData) {
              debugLog.info('Loaded from local storage fallback');
            }
          }
        }

        // If we have assessment from history, save importance data for JobValueCompass
        if (assessmentFromHistory && assessmentFromHistory.importanceRatings) {
          const normalizedImportance = normalizeImportanceData(assessmentFromHistory);
          if (normalizedImportance) {
            // Save to cross-device storage for JobValueCompass to use
            await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, normalizedImportance);
            await setCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, assessmentFromHistory.importanceSkipped || false);
          }
        }

        debugLog.info('Raw assessment data loaded', rawAssessmentData);
        debugLog.deviceInfo();

        // Normalize the data structure to handle both formats
        const normalizedResults = normalizeAssessmentData(rawAssessmentData);

        if (!normalizedResults) {
          // Don't throw an error - instead show a helpful message
          debugLog.warn('No assessment results found in any storage location');
          setError('No assessment results found. Please complete an assessment first.');
          setIsLoading(false);
          return;
        }

        debugLog.info('Normalized results', normalizedResults);
        setResults(normalizedResults);

        // Show save dialog only for new assessments (not from profile history)
        const hasShownSaveDialog = await getCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
        const isFromProfileHistory = !!location.state?.assessment;

        // Only show dialog for fresh assessments from localStorage, not from profile history
        // AND only if user is authenticated (not anonymous)
        const shouldShowDialog = !hasShownSaveDialog && !isFromProfileHistory && 
                                !!currentUser && !authenticationService.isAnonymous();
        setShowSaveDialog(shouldShowDialog);

      } catch (err) {
        console.error('Error loading results:', err);
        setError(err instanceof Error ? err.message : 'Failed to load results');
        // Don't auto-redirect - let user choose what to do
      } finally {
        setIsLoading(false);
      }
    };

    loadResults();
  }, [location.state, currentUser, navigate]);

  const handleSaveAssessment = async () => {
    if (!results || !currentUser) return;

    if (!companyName.trim() || !industry) {
      toast({
        title: 'Missing Information',
        description: 'Please fill in the required fields (Company Name and Industry).',
        variant: 'destructive',
      });
      return;
    }

    setIsSaving(true);
    try {
      // Get the actual satisfaction responses from cross-device storage
      const satisfactionRatings = await getCrossDeviceStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, {});
      const importanceRatings = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, {});
      const importanceSkipped = await getCrossDeviceStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);

      const assessmentData: AssessmentData = {
        id: '', // Will be set by Firestore
        userId: currentUser.uid,
        companyName: companyName.trim(),
        industry,
        ...(jobTitle.trim() && { jobTitle: jobTitle.trim() }),
        ...(department.trim() && { department: department.trim() }),
        importanceRatings,
        satisfactionRatings,
        jobSatisfactionResults: {
          totalScore: getScoreValue(results),
          categories: Object.entries(results.categoryScores || {}).map(([name, score]) => ({
            name,
            score: score.score,
            weight: 0.1, // Default weight
            color: '#3b82f6' // Default color
          })),
          interpretation: {
            level: getScoreValue(results) >= 60 ? 'Good' : getScoreValue(results) >= 40 ? 'Fair' : 'Poor',
            message: `Your job satisfaction score is ${getScoreValue(results)}`
          }
        },
        step: 'results',
        importanceSkipped,
        completedAt: new Date(),
        createdAt: Timestamp.now(),
        updatedAt: Timestamp.now(),
      };

      await saveAssessment(assessmentData);

      if (industry) {
        try {
          const comparison = await compareToIndustry(assessmentData);
          setIndustryComparison(comparison);
        } catch (error) {
          console.warn('Failed to get industry comparison:', error);
        }
      }

      // Mark save dialog as shown so it doesn't appear again
      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, true);
      setShowSaveDialog(false);

      toast({
        title: 'Assessment Saved!',
        description: 'Your results have been saved to your profile.',
      });
    } catch (error) {
      console.error('Error saving assessment:', error);
      toast({
        title: 'Save Failed',
        description: 'There was an error saving your assessment. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsSaving(false);
    }
  };

  const handleBack = () => {
    navigate('/dashboard');
  };

  const handleCancelSave = async () => {
    // Mark save dialog as shown so it doesn't appear again
    await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, true);
    setShowSaveDialog(false);
  };

  // Categories are handled properly by getCategoryDisplayName()

  const topCategories = Object.entries(results?.categoryScores || {})
    .sort(([,a], [,b]) => b.score - a.score)
    .slice(0, 3);

  const bottomCategories = Object.entries(results?.categoryScores || {})
    .sort(([,a], [,b]) => a.score - b.score)
    .slice(0, 3);


  if (isLoading) {
    return (
      <BackgroundWrapper variant="background-responsive" minHeight="screen" className="flex items-center justify-center">
        <div className="text-center">
          <p className="text-lg text-white">Loading your results...</p>
        </div>
      </BackgroundWrapper>
    );
  }

  if (error) {
    return (
      <BackgroundWrapper variant="background-responsive" minHeight="screen" className="flex flex-col">
        <Header />
        <div className="flex-1 flex items-center justify-center p-4">
          <Card className="max-w-md backdrop-blur-xl bg-white/90 border-white/20 shadow-2xl">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                No Results Found
              </CardTitle>
              <CardDescription>
                We couldn't find any assessment results to display.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-gray-600">
                This can happen if:
              </p>
              <ul className="text-sm text-gray-600 space-y-2 ml-4 list-disc">
                <li>You haven't completed an assessment yet</li>
                <li>Your session expired or the browser tab was closed</li>
                <li>Your browser cleared the stored data</li>
                <li>You accessed this page directly without completing an assessment</li>
              </ul>

              <div className="space-y-3 pt-4">
                <Button
                  onClick={() => navigate('/assessment')}
                  className="w-full gradient-bg text-white"
                >
                  <Target className="h-4 w-4 mr-2" />
                  Start New Assessment
                </Button>
                <Button
                  onClick={() => navigate('/dashboard')}
                  variant="outline"
                  className="w-full"
                >
                  Return to Dashboard
                </Button>
              </div>

            </CardContent>
          </Card>
        </div>
      </BackgroundWrapper>
    );
  }

  if (!results) {
    return null;
  }

  return (
    <BackgroundWrapper variant="background-responsive" minHeight="screen" className="flex flex-col">
      <Header />

      {/* Compact header section */}
      <div className="container mx-auto px-4 py-2">
        <div className="flex justify-between items-center">
          <button
            onClick={handleBack}
            className="h-10 w-10 rounded-full backdrop-blur-xl bg-white/20 hover:bg-white/30 border border-white/30 flex items-center justify-center transition-all duration-200 hover:scale-105 active:scale-95 shadow-lg"
            aria-label="Back to dashboard"
          >
            <ChevronLeft className="h-5 w-5 text-white" />
          </button>

          {currentUser && !location.state?.assessment && (
            <Dialog open={showSaveDialog} onOpenChange={(open) => {
              if (!open) {
                handleCancelSave();
              } else {
                setShowSaveDialog(open);
              }
            }}>
              <DialogTrigger asChild>
                <Button variant="secondary" size="sm" className="bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md">
                  <Save className="mr-2 h-4 w-4" />
                  Save Results
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                  <DialogTitle>Save Your Assessment</DialogTitle>
                  <DialogDescription>
                    Save this assessment to track your job satisfaction over time.
                  </DialogDescription>
                </DialogHeader>
                <div className="grid gap-3 py-4">
                  <div className="grid gap-2">
                    <Label htmlFor="company">Company Name *</Label>
                    <Input
                      id="company"
                      value={companyName}
                      onChange={(e) => setCompanyName(e.target.value)}
                      placeholder="Enter your company name"
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="industry">Industry *</Label>
                    <Select value={industry} onValueChange={setIndustry}>
                      <SelectTrigger id="industry">
                        <SelectValue placeholder="Select your industry" />
                      </SelectTrigger>
                      <SelectContent>
                        {INDUSTRY_CATEGORIES.map(cat => (
                          <SelectItem key={cat} value={cat}>
                            {cat}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="jobTitle">Job Title (Optional)</Label>
                    <Input
                      id="jobTitle"
                      value={jobTitle}
                      onChange={(e) => setJobTitle(e.target.value)}
                      placeholder="e.g., Software Engineer"
                    />
                  </div>
                </div>
                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={handleCancelSave}>
                    Cancel
                  </Button>
                  <Button onClick={handleSaveAssessment} disabled={isSaving}>
                    {isSaving ? 'Saving...' : 'Save Assessment'}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          )}
        </div>
      </div>

      {/* Main content - compact layout */}
      <main className="flex-1 container mx-auto px-4 pb-4">
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">

            {/* Left Column - Score Overview */}
            <div className="lg:col-span-1 space-y-4">
              {/* Overall Score Card */}
              <Card 
                className="backdrop-blur-xl border-white/20 shadow-2xl"
                style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(20px)'
                }}
              >
                <CardHeader className="text-center pb-3">
                  <CardTitle className="text-2xl font-bold text-gray-900">Your Score</CardTitle>
                </CardHeader>
                <CardContent className="text-center">
                  <ScoreDisplay 
                    score={getScoreValue(results)}
                    size="lg"
                    showTooltip={true}
                    className="justify-center mb-4"
                  />
                  <div className="w-full bg-gray-200 rounded-full h-3">
                    <div 
                      className={`h-3 rounded-full transition-all duration-500 ${getScoreBackgroundColor(transformScoreForDisplay(getScoreValue(results)))}`}
                      style={{ width: `${Math.max(5, ((getScoreValue(results) + 88) / 176) * 100)}%` }}
                    />
                  </div>
                  <div className="mt-4">
                    <ScoreExplainer score={getScoreValue(results)} showAsDialog={true} />
                  </div>
                </CardContent>
              </Card>

              {/* Top Strengths */}
              <Card 
                className="backdrop-blur-xl border-white/20 shadow-2xl"
                style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(20px)'
                }}
              >
                <CardHeader className="pb-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Award className="h-5 w-5 text-green-600" />
                    Top Strengths
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    {topCategories.map(([category, score], index) => (
                      <div key={category} className="flex justify-between items-center">
                        <span className="text-sm font-medium text-gray-700">
                          {getCategoryDisplayName(category)}
                        </span>
                        <ScoreDisplay 
                          score={score.score}
                          size="sm"
                          showLabel={false}
                          showTooltip={false}
                          className="text-sm"
                        />
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Areas for Improvement */}
              <Card 
                className="backdrop-blur-xl border-white/20 shadow-2xl"
                style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(20px)'
                }}
              >
                <CardHeader className="pb-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Target className="h-5 w-5 text-orange-600" />
                    Focus Areas
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    {bottomCategories.map(([category, score]) => (
                      <div key={category} className="flex justify-between items-center">
                        <span className="text-sm font-medium text-gray-700">
                          {getCategoryDisplayName(category)}
                        </span>
                        <ScoreDisplay 
                          score={score.score}
                          size="sm"
                          showLabel={false}
                          showTooltip={false}
                          className="text-sm"
                        />
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Right Column - Detailed View */}
            <div className="lg:col-span-2">
              <Card 
                className="backdrop-blur-xl border-white/20 shadow-2xl"
                style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(20px)'
                }}
              >
                <CardHeader className="pb-3">
                  <CardTitle className="text-xl font-bold text-gray-900">Detailed Analysis</CardTitle>
                </CardHeader>
                <CardContent>
                  <Tabs defaultValue="compass" className="w-full">
                    <TabsList className="grid w-full grid-cols-3 mb-4">
                      <TabsTrigger value="compass" className="text-xs sm:text-sm">
                        <span className="hidden xs:inline">Value Compass</span>
                        <span className="xs:hidden">Compass</span>
                      </TabsTrigger>
                      <TabsTrigger value="breakdown" className="text-xs sm:text-sm">Categories</TabsTrigger>
                      <TabsTrigger value="insights" className="text-xs sm:text-sm">Insights</TabsTrigger>
                    </TabsList>

                    <TabsContent value="compass" className="py-4">
                      <div className="flex items-center justify-center min-h-[400px]">
                        <JobValueCompass />
                      </div>
                    </TabsContent>

                    <TabsContent value="breakdown" className="py-4">
                      <DetailedResults categoryScores={results.categoryScores} />
                    </TabsContent>

                    <TabsContent value="insights" className="py-4">
                      <CoachingInsights categoryScores={results.categoryScores} />
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </main>
    </BackgroundWrapper>
  );
};

export default ResultsEnhanced;

