#!/usr/bin/env node

/**
 * COMPREHENSIVE ASSESSMENT SYSTEM TEST
 * 
 * This test script validates the complete assessment flow including:
 * - Firebase emulator connectivity
 * - Authentication service
 * - Assessment data flow (importance -> satisfaction -> results)
 * - Storage persistence (local, cross-device, Firebase)
 * - Data normalization and transformation
 * - Error handling and timeout scenarios
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

// Test configuration
const TEST_CONFIG = {
  FIREBASE_EMULATOR_PORTS: {
    auth: 9797,
    firestore: 8785,
    ui: 4700
  },
  TIMEOUTS: {
    firebase: 5000,
    auth: 3000,
    overall: 30000
  },
  TEST_USER_DATA: {
    userId: 'test_user_123',
    assessmentId: `test_assessment_${Date.now()}`,
    importance: {
      purpose: 5,
      growth: 4,
      compensation: 3,
      balance: 5,
      support: 4,
      culture: 3,
      resources: 2,
      recognition: 3,
      security: 4,
      autonomy: 5
    },
    satisfaction: {
      purpose: 3,
      growth: 2,
      compensation: 1,
      balance: 2,
      support: 4,
      culture: 3,
      resources: 3,
      recognition: 2,
      security: 3,
      autonomy: 4
    }
  }
};

// Test results tracking
const testResults = {
  total: 0,
  passed: 0,
  failed: 0,
  errors: []
};

// Utility functions
function log(message, type = 'info') {
  const timestamp = new Date().toISOString();
  const prefix = {
    info: '✅',
    warn: '⚠️',
    error: '❌',
    test: '🧪'
  }[type] || 'ℹ️';

  console.log(`${prefix} [${timestamp}] ${message}`);
}

function runTest(testName, testFunction) {
  testResults.total++;
  log(`Running test: ${testName}`, 'test');

  try {
    const result = testFunction();
    if (result !== false) {
      testResults.passed++;
      log(`✅ PASSED: ${testName}`);
      return true;
    } else {
      testResults.failed++;
      testResults.errors.push(`FAILED: ${testName}`);
      log(`❌ FAILED: ${testName}`, 'error');
      return false;
    }
  } catch (error) {
    testResults.failed++;
    testResults.errors.push(`ERROR in ${testName}: ${error.message}`);
    log(`❌ ERROR in ${testName}: ${error.message}`, 'error');
    return false;
  }
}

// Test 1: Check if Firebase emulators are running
function testFirebaseEmulators() {
  log('Checking Firebase emulator connectivity...');

  try {
    // Check if emulator ports are listening
    const portCheckCommand = `lsof -i :${TEST_CONFIG.FIREBASE_EMULATOR_PORTS.firestore} -i :${TEST_CONFIG.FIREBASE_EMULATOR_PORTS.auth}`;
    const portResult = execSync(portCheckCommand, { encoding: 'utf8', timeout: 5000 });

    const hasFirestore = portResult.includes(`:${TEST_CONFIG.FIREBASE_EMULATOR_PORTS.firestore}`);
    const hasAuth = portResult.includes(`:${TEST_CONFIG.FIREBASE_EMULATOR_PORTS.auth}`);

    if (hasFirestore && hasAuth) {
      log('Firebase emulators are running (Firestore & Auth)');
      return true;
    } else {
      log(`Missing emulators - Firestore: ${hasFirestore}, Auth: ${hasAuth}`, 'error');
      return false;
    }
  } catch (error) {
    log(`Failed to check emulator ports: ${error.message}`, 'error');
    return false;
  }
}

// Test 2: Validate project file structure
function testProjectStructure() {
  log('Validating project file structure...');

  const requiredFiles = [
    'src/services/assessmentService.ts',
    'src/services/authenticationService.ts',
    'src/components/AssessmentForm.tsx',
    'src/components/ImportanceForm.tsx',
    'src/pages/ResultsEnhanced.tsx',
    'src/utils/crossDeviceStorage.ts',
    'src/utils/scoreCalculation.ts',
    'firebase.json',
    'firestore.rules'
  ];

  const missingFiles = [];

  for (const file of requiredFiles) {
    const fullPath = path.join(process.cwd(), file);
    if (!fs.existsSync(fullPath)) {
      missingFiles.push(file);
    }
  }

  if (missingFiles.length === 0) {
    log('All required files are present');
    return true;
  } else {
    log(`Missing files: ${missingFiles.join(', ')}`, 'error');
    return false;
  }
}

// Test 3: Check for TypeScript/compilation errors
function testTypeScriptCompilation() {
  log('Checking TypeScript compilation...');

  try {
    // Run TypeScript check
    execSync('npx tsc --noEmit --skipLibCheck', { 
      encoding: 'utf8', 
      timeout: 30000,
      stdio: 'pipe'
    });
    log('TypeScript compilation successful');
    return true;
  } catch (error) {
    log(`TypeScript compilation errors detected: ${error.stdout || error.message}`, 'error');
    return false;
  }
}

// Test 4: Validate assessment data flow logic
function testAssessmentDataFlow() {
  log('Testing assessment data flow logic...');

  try {
    // Read and validate scoreCalculation utility
    const scoreCalcPath = path.join(process.cwd(), 'src/utils/scoreCalculation.ts');
    const scoreCalcContent = fs.readFileSync(scoreCalcPath, 'utf8');

    // Check for required functions
    const requiredFunctions = [
      'calculateJobSatisfaction',
      'calculateCategoryScores',
      'normalizeScore'
    ];

    const missingFunctions = requiredFunctions.filter(func => 
      !scoreCalcContent.includes(func)
    );

    if (missingFunctions.length > 0) {
      log(`Missing score calculation functions: ${missingFunctions.join(', ')}`, 'error');
      return false;
    }

    // Check for proper category handling
    const hasCategoryProcessing = scoreCalcContent.includes('category') && 
                                   scoreCalcContent.includes('importance') &&
                                   scoreCalcContent.includes('satisfaction');

    if (!hasCategoryProcessing) {
      log('Score calculation missing category processing logic', 'error');
      return false;
    }

    log('Assessment data flow logic validated');
    return true;
  } catch (error) {
    log(`Error validating data flow: ${error.message}`, 'error');
    return false;
  }
}

// Test 5: Validate storage utilities
function testStorageUtilities() {
  log('Testing storage utilities...');

  try {
    const storagePath = path.join(process.cwd(), 'src/utils/crossDeviceStorage.ts');
    const storageContent = fs.readFileSync(storagePath, 'utf8');

    // Check for required storage functions
    const requiredStorageFunctions = [
      'setCrossDeviceStorageItem',
      'getCrossDeviceStorageItem',
      'removeCrossDeviceStorageItem'
    ];

    const missingStorageFunctions = requiredStorageFunctions.filter(func => 
      !storageContent.includes(func)
    );

    if (missingStorageFunctions.length > 0) {
      log(`Missing storage functions: ${missingStorageFunctions.join(', ')}`, 'error');
      return false;
    }

    // Check for error handling
    const hasErrorHandling = storageContent.includes('try') && 
                            storageContent.includes('catch') &&
                            storageContent.includes('localStorage');

    if (!hasErrorHandling) {
      log('Storage utilities missing error handling', 'error');
      return false;
    }

    log('Storage utilities validated');
    return true;
  } catch (error) {
    log(`Error validating storage: ${error.message}`, 'error');
    return false;
  }
}

// Test 6: Check Firebase configuration
function testFirebaseConfiguration() {
  log('Testing Firebase configuration...');

  try {
    // Check firebase.json
    const firebaseConfigPath = path.join(process.cwd(), 'firebase.json');
    const firebaseConfig = JSON.parse(fs.readFileSync(firebaseConfigPath, 'utf8'));

    // Validate emulator configuration
    if (!firebaseConfig.emulators) {
      log('Firebase config missing emulators section', 'error');
      return false;
    }

    const requiredEmulators = ['auth', 'firestore'];
    const missingEmulators = requiredEmulators.filter(emu => 
      !firebaseConfig.emulators[emu]
    );

    if (missingEmulators.length > 0) {
      log(`Firebase config missing emulators: ${missingEmulators.join(', ')}`, 'error');
      return false;
    }

    // Check Firestore rules
    const rulesPath = path.join(process.cwd(), 'firestore.rules');
    if (!fs.existsSync(rulesPath)) {
      log('Firestore rules file missing', 'error');
      return false;
    }

    const rulesContent = fs.readFileSync(rulesPath, 'utf8');
    if (!rulesContent.includes('assessments') || !rulesContent.includes('users')) {
      log('Firestore rules missing assessment/user rules', 'error');
      return false;
    }

    log('Firebase configuration validated');
    return true;
  } catch (error) {
    log(`Error validating Firebase config: ${error.message}`, 'error');
    return false;
  }
}

// Test 7: Validate assessment form structure
function testAssessmentFormStructure() {
  log('Testing assessment form structure...');

  try {
    // Check ImportanceForm
    const importanceFormPath = path.join(process.cwd(), 'src/components/ImportanceForm.tsx');
    const importanceFormContent = fs.readFileSync(importanceFormPath, 'utf8');

    // Check for required elements
    const requiredImportanceElements = [
      'handleSubmit',
      'setCrossDeviceStorageItem',
      'STORAGE_KEYS.IMPORTANCE_RESULTS',
      'assessmentService',
      'timeout'
    ];

    const missingImportanceElements = requiredImportanceElements.filter(element => 
      !importanceFormContent.includes(element)
    );

    if (missingImportanceElements.length > 0) {
      log(`ImportanceForm missing elements: ${missingImportanceElements.join(', ')}`, 'error');
      return false;
    }

    // Check AssessmentForm
    const assessmentFormPath = path.join(process.cwd(), 'src/components/AssessmentForm.tsx');
    const assessmentFormContent = fs.readFileSync(assessmentFormPath, 'utf8');

    const requiredAssessmentElements = [
      'handleSubmit',
      'setCrossDeviceStorageItem',
      'STORAGE_KEYS.SATISFACTION_RESPONSES',
      'calculateJobSatisfaction',
      'authenticationService'
    ];

    const missingAssessmentElements = requiredAssessmentElements.filter(element => 
      !assessmentFormContent.includes(element)
    );

    if (missingAssessmentElements.length > 0) {
      log(`AssessmentForm missing elements: ${missingAssessmentElements.join(', ')}`, 'error');
      return false;
    }

    log('Assessment form structure validated');
    return true;
  } catch (error) {
    log(`Error validating forms: ${error.message}`, 'error');
    return false;
  }
}

// Test 8: Check results page data handling
function testResultsPageDataHandling() {
  log('Testing results page data handling...');

  try {
    const resultsPath = path.join(process.cwd(), 'src/pages/ResultsEnhanced.tsx');
    const resultsContent = fs.readFileSync(resultsPath, 'utf8');

    // Check for required data handling functions
    const requiredResultsElements = [
      'normalizeAssessmentData',
      'getCrossDeviceStorageItem',
      'assessmentService.getMostRecentResults',
      'getNavigationState',
      'isIOSDevice'
    ];

    const missingResultsElements = requiredResultsElements.filter(element => 
      !resultsContent.includes(element)
    );

    if (missingResultsElements.length > 0) {
      log(`ResultsEnhanced missing elements: ${missingResultsElements.join(', ')}`, 'error');
      return false;
    }

    // Check for error handling
    const hasErrorHandling = resultsContent.includes('try') && 
                            resultsContent.includes('catch') &&
                            resultsContent.includes('setError');

    if (!hasErrorHandling) {
      log('Results page missing error handling', 'error');
      return false;
    }

    log('Results page data handling validated');
    return true;
  } catch (error) {
    log(`Error validating results page: ${error.message}`, 'error');
    return false;
  }
}

// Test 9: Validate environment setup
function testEnvironmentSetup() {
  log('Testing environment setup...');

  try {
    // Check for .env files
    const envFiles = ['.env', '.env.local', '.env.development'];
    let hasEnvFile = false;

    for (const envFile of envFiles) {
      if (fs.existsSync(path.join(process.cwd(), envFile))) {
        hasEnvFile = true;
        break;
      }
    }

    if (!hasEnvFile) {
      log('No environment files found (.env, .env.local, .env.development)', 'warn');
    }

    // Check package.json dependencies
    const packagePath = path.join(process.cwd(), 'package.json');
    const packageContent = JSON.parse(fs.readFileSync(packagePath, 'utf8'));

    const requiredDependencies = [
      'firebase',
      'react',
      'typescript',
      '@types/react'
    ];

    const allDeps = {
      ...packageContent.dependencies,
      ...packageContent.devDependencies
    };

    const missingDeps = requiredDependencies.filter(dep => !allDeps[dep]);

    if (missingDeps.length > 0) {
      log(`Missing dependencies: ${missingDeps.join(', ')}`, 'error');
      return false;
    }

    log('Environment setup validated');
    return true;
  } catch (error) {
    log(`Error validating environment: ${error.message}`, 'error');
    return false;
  }
}

// Test 10: Check for known issue patterns
function testKnownIssuePatterns() {
  log('Checking for known issue patterns...');

  try {
    const issuePatterns = [
      {
        file: 'src/components/AssessmentForm.tsx',
        pattern: 'timeout',
        description: 'Timeout handling in AssessmentForm'
      },
      {
        file: 'src/components/ImportanceForm.tsx',
        pattern: 'timeout',
        description: 'Timeout handling in ImportanceForm'
      },
      {
        file: 'src/pages/ResultsEnhanced.tsx',
        pattern: 'normalizeAssessmentData',
        description: 'Data normalization in results'
      },
      {
        file: 'src/services/assessmentService.ts',
        pattern: 'fallback',
        description: 'Fallback handling in assessment service'
      }
    ];

    const foundIssues = [];

    for (const pattern of issuePatterns) {
      const filePath = path.join(process.cwd(), pattern.file);
      if (fs.existsSync(filePath)) {
        const content = fs.readFileSync(filePath, 'utf8');
        if (content.includes(pattern.pattern)) {
          log(`✅ Found ${pattern.description}`);
        } else {
          foundIssues.push(pattern.description);
        }
      } else {
        foundIssues.push(`Missing file: ${pattern.file}`);
      }
    }

    if (foundIssues.length > 0) {
      log(`Missing issue patterns: ${foundIssues.join(', ')}`, 'error');
      return false;
    }

    log('Known issue patterns validated');
    return true;
  } catch (error) {
    log(`Error checking issue patterns: ${error.message}`, 'error');
    return false;
  }
}

// Main test runner
async function runAllTests() {
  log('🚀 Starting Comprehensive Assessment System Test', 'test');
  log(`Testing against configuration: ${JSON.stringify(TEST_CONFIG, null, 2)}`);

  // Run all tests
  runTest('Firebase Emulator Connectivity', testFirebaseEmulators);
  runTest('Project File Structure', testProjectStructure);
  runTest('TypeScript Compilation', testTypeScriptCompilation);
  runTest('Assessment Data Flow Logic', testAssessmentDataFlow);
  runTest('Storage Utilities', testStorageUtilities);
  runTest('Firebase Configuration', testFirebaseConfiguration);
  runTest('Assessment Form Structure', testAssessmentFormStructure);
  runTest('Results Page Data Handling', testResultsPageDataHandling);
  runTest('Environment Setup', testEnvironmentSetup);
  runTest('Known Issue Patterns', testKnownIssuePatterns);

  // Generate test report
  log('\n📊 TEST RESULTS SUMMARY', 'test');
  log(`Total Tests: ${testResults.total}`);
  log(`Passed: ${testResults.passed}`);
  log(`Failed: ${testResults.failed}`);
  log(`Success Rate: ${((testResults.passed / testResults.total) * 100).toFixed(1)}%`);

  if (testResults.failed > 0) {
    log('\n❌ FAILED TESTS:', 'error');
    testResults.errors.forEach(error => log(`  - ${error}`, 'error'));
  }

  // Recommendations based on test results
  log('\n🔧 RECOMMENDATIONS:', 'test');

  if (testResults.passed === testResults.total) {
    log('✅ All tests passed! Assessment system appears to be properly configured.');
    log('🔍 Next steps: Run integration tests with actual user flow simulation.');
  } else {
    log('⚠️ Some tests failed. Address the issues above before proceeding.');
    log('🔍 Focus on the failed tests as they may be causing the assessment flow issues.');
  }

  // Save detailed test report
  const reportPath = path.join(process.cwd(), 'test_report.json');
  const report = {
    timestamp: new Date().toISOString(),
    config: TEST_CONFIG,
    results: testResults,
    recommendations: testResults.passed === testResults.total ? 
      ['All tests passed', 'Run integration tests'] :
      ['Fix failed tests', 'Re-run validation']
  };

  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
  log(`📄 Detailed test report saved to: ${reportPath}`);

  return testResults.passed === testResults.total;
}

// Run the tests
if (require.main === module) {
  runAllTests().then(success => {
    process.exit(success ? 0 : 1);
  }).catch(error => {
    log(`Fatal test error: ${error.message}`, 'error');
    process.exit(1);
  });
}

module.exports = { runAllTests, testResults };

