/**
 * Calculate category scores from responses
 * @param responses All category responses
 * @param importanceAnswers Optional importance ratings
 * @returns Category scores object
 */
export function calculateCategoryScores(
  responses: AllResponses,
  importanceAnswers?: { category: string; importance: number; weight: number }[] | null
): AllCategoryScores {
  const categoryScores: AllCategoryScores = {};
  const isImportanceSkipped = !importanceAnswers;

  // Process each category
  for (const [category, questions] of Object.entries(responses)) {
    // Normalize category ID to handle legacy names
    const normalizedCategory = normalizeCategoryId(category);
    const categoryQuestionScores: number[] = [];

    // Process each question in the category
    for (const [questionId, response] of Object.entries(questions)) {
      // Get importance rating (default to 3 if not provided)
      const importanceRating = importanceAnswers?.find(
        ans => normalizeCategoryId(ans.category) === normalizedCategory
      )?.importance ?? 3;

      // Calculate score using new logic
      const score = calculateQuestionScore(
        response.response,
        importanceRating,
        0, // No bother level
        isImportanceSkipped
      );

      // Store raw scores for category calculation
      categoryQuestionScores.push(score);
    }

    // Calculate category score (raw, without weight)
    if (categoryQuestionScores.length > 0) {
      const rawCategoryScore = calculateFinalScore(categoryQuestionScores, 'average');

      // Store the importance rating with the category score if available
      const importanceRating = importanceAnswers?.find(
        ans => normalizeCategoryId(ans.category) === normalizedCategory
      )?.importance;

      categoryScores[normalizedCategory] = {
        score: rawCategoryScore,
        ...(importanceRating !== undefined && { importance: importanceRating })
      };
    }
  }

  return categoryScores;
}

/**
 * Normalize a score to a specific range
 * @param score Raw score to normalize
 * @param minRange Target minimum value (default: -88)
 * @param maxRange Target maximum value (default: 88)
 * @returns Normalized score within the specified range
 */
export function normalizeScore(
  score: number,
  minRange: number = MIN_SCORE,
  maxRange: number = MAX_SCORE
): number {
  // Clamp the score to the valid range
  return clampScore(score);
}

/**
 * Normalize score to a 0-100 percentage scale
 * @param score Raw score (-88 to 88)
 * @returns Percentage score (0 to 100)
 */
export function normalizeToPercentage(score: number): number {
  // First clamp to valid range
  const clampedScore = clampScore(score);

  // Convert from -88/88 range to 0-100 range
  // -88 becomes 0, 0 becomes ~50, 88 becomes 100
  return Math.round(((clampedScore + SCALE_FACTOR) / (2 * SCALE_FACTOR)) * 100);
}

/**
 * Normalize score to a 1-5 scale (like star ratings)
 * @param score Raw score (-88 to 88)
 * @returns Score on 1-5 scale
 */
export function normalizeToFivePoint(score: number): number {
  // First clamp to valid range
  const clampedScore = clampScore(score);

  // Convert from -88/88 range to 1-5 range
  const normalized = ((clampedScore + SCALE_FACTOR) / (2 * SCALE_FACTOR)) * 4 + 1;

  // Round to nearest 0.1 and clamp to 1-5 range
  return Math.max(1, Math.min(5, Math.round(normalized * 10) / 10));
}

