#!/usr/bin/env node

/**
 * INTEGRATION TEST: Complete Assessment Flow Simulation
 * 
 * This test simulates the complete user journey through the assessment system:
 * 1. Start importance assessment (ImportanceForm)
 * 2. Complete satisfaction assessment (AssessmentForm)  
 * 3. Process and save results (Firebase + local storage)
 * 4. Load and display results (ResultsEnhanced)
 * 5. Validate data persistence across all storage layers
 * 
 * Tests both successful flows and error scenarios.
 */

import { execSync, spawn } from 'child_process';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// ES module compatibility
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Test configuration
const INTEGRATION_CONFIG = {
  DEV_SERVER_PORT: 5173,
  DEV_SERVER_TIMEOUT: 30000,
  ASSESSMENT_TIMEOUT: 60000,
  TEST_DATA: {
    importance: {
      purpose: 5,
      growth: 4,
      compensation: 3,
      balance: 5,
      support: 4,
      culture: 3,
      resources: 2,
      recognition: 3,
      security: 4,
      autonomy: 5
    },
    satisfaction: {
      purpose: 3,
      growth: 2,
      compensation: 1,
      balance: 2,
      support: 4,
      culture: 3,
      resources: 3,
      recognition: 2,
      security: 3,
      autonomy: 4
    }
  }
};

// Test state tracking
const integrationState = {
  devServerProcess: null,
  testResults: [],
  errors: []
};

function log(message, type = 'info') {
  const timestamp = new Date().toISOString();
  const prefix = {
    info: '🔍',
    success: '✅',
    warn: '⚠️',
    error: '❌',
    test: '🧪',
    server: '🌐',
    firebase: '🔥'
  }[type] || 'ℹ️';

  console.log(`${prefix} [${timestamp}] ${message}`);
}

function addTestResult(testName, passed, details = null) {
  integrationState.testResults.push({
    name: testName,
    passed,
    details,
    timestamp: new Date().toISOString()
  });

  if (passed) {
    log(`✅ PASSED: ${testName}`, 'success');
  } else {
    log(`❌ FAILED: ${testName}`, 'error');
    if (details) {
      log(`   Details: ${details}`, 'error');
    }
    integrationState.errors.push(`${testName}: ${details}`);
  }
}

// Test 1: Start development server
async function startDevServer() {
  log('Starting development server for integration testing...', 'server');

  return new Promise((resolve, reject) => {
    // Start the dev server
    const devProcess = spawn('npm', ['run', 'dev'], {
      stdio: 'pipe',
      cwd: process.cwd()
    });

    integrationState.devServerProcess = devProcess;

    let startupOutput = '';

