          encoding: 'utf8',
          timeout: 3000
        });

        if (response.trim() === '200') {
          successfulRoutes++;
          log(`Route ${route}: OK`, 'info');
        } else {
          log(`Route ${route}: HTTP ${response.trim()}`, 'warn');
        }
      } catch (error) {
        log(`Route ${route}: Error - ${error.message}`, 'warn');
      }
    }

    if (successfulRoutes >= 4) { // Allow for some flexibility
      addTestResult('Assessment Routing', true, `${successfulRoutes}/${routes.length} routes accessible`);
      return true;
    } else {
      addTestResult('Assessment Routing', false, `Only ${successfulRoutes}/${routes.length} routes accessible`);
      return false;
    }

  } catch (error) {
    addTestResult('Assessment Routing', false, error.message);
    return false;
  }
}

// Cleanup function
function cleanup() {
  log('Cleaning up integration test resources...', 'info');

  if (integrationState.devServerProcess) {
    integrationState.devServerProcess.kill();
    log('Development server stopped', 'server');
  }

  // Clean up any temporary files
  const tempFiles = [
    'temp_localStorage_test.js',
    'temp_score_test.js'
  ];

  tempFiles.forEach(file => {
    const filePath = path.join(process.cwd(), file);
    if (fs.existsSync(filePath)) {
      fs.unlinkSync(filePath);
    }
  });
}

// Generate comprehensive report
function generateReport() {
  const passed = integrationState.testResults.filter(test => test.passed).length;
  const failed = integrationState.testResults.filter(test => !test.passed).length;
  const total = integrationState.testResults.length;
  const successRate = ((passed / total) * 100).toFixed(1);

  log('\\n📊 INTEGRATION TEST RESULTS', 'test');
  log(`Total Tests: ${total}`);
  log(`Passed: ${passed}`);
  log(`Failed: ${failed}`);
  log(`Success Rate: ${successRate}%`);

  if (failed > 0) {
    log('\\n❌ FAILED TESTS:', 'error');
    integrationState.testResults
      .filter(test => !test.passed)
      .forEach(test => {
        log(`  - ${test.name}: ${test.details}`, 'error');
      });
  }

  // Save detailed report
  const report = {
    timestamp: new Date().toISOString(),
    config: INTEGRATION_CONFIG,
    summary: {
      total,
      passed,
      failed,
      successRate: parseFloat(successRate)
    },
    tests: integrationState.testResults,
    errors: integrationState.errors
  };

  const reportPath = path.join(process.cwd(), 'integration_test_report.json');
  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
  log(`📄 Integration test report saved to: ${reportPath}`);

  // Provide recommendations
  log('\\n🔧 RECOMMENDATIONS:', 'test');
  if (successRate === '100.0') {
    log('✅ All integration tests passed! Assessment system is working correctly.');
    log('🔍 System is ready for user testing and deployment.');
  } else if (successRate >= '80.0') {
    log('⚠️ Most tests passed, but some issues need attention.');
    log('🔍 Review failed tests and address critical issues.');
  } else {
    log('❌ Multiple integration test failures detected.');
    log('🔍 System requires significant fixes before user testing.');
  }

  return successRate === '100.0';
}

// Main integration test runner
async function runIntegrationTests() {
  log('🚀 Starting Integration Test Suite', 'test');
  log('This will test the complete assessment flow with real components', 'info');

  // Set up cleanup on exit
  process.on('SIGINT', cleanup);
  process.on('SIGTERM', cleanup);
  process.on('exit', cleanup);

  try {
    // Run tests in sequence
    log('\\n=== PHASE 1: INFRASTRUCTURE TESTS ===', 'test');
    await startDevServer();
    await new Promise(resolve => setTimeout(resolve, 3000)); // Wait for server to stabilize
    await testServerAccessibility();
    await testFirebaseConnectivity();

    log('\\n=== PHASE 2: FUNCTIONAL TESTS ===', 'test');
    await testLocalStorageOperations();
    await testScoreCalculation();

    log('\\n=== PHASE 3: INTEGRATION TESTS ===', 'test');
    await testAssessmentRouting();

    // Generate final report
    const allTestsPassed = generateReport();

    cleanup();

    return allTestsPassed;

  } catch (error) {
    log(`Fatal integration test error: ${error.message}`, 'error');
    cleanup();
    return false;
  }
}

// Run the integration tests
if (import.meta.url === `file://${process.argv[1]}`) {
  runIntegrationTests().then(success => {
    process.exit(success ? 0 : 1);
  }).catch(error => {
    log(`Fatal error: ${error.message}`, 'error');
    cleanup();
    process.exit(1);
  });
}

export { runIntegrationTests };

