    if (response.includes('<div id="root">') || response.includes('<!DOCTYPE html>')) {
      addTestResult('Homepage Access', true, 'Homepage loads with proper HTML structure');
      return true;
    } else {
      addTestResult('Homepage Access', false, 'Homepage response does not contain expected HTML structure');
      return false;
    }
  } catch (error) {
    addTestResult('Homepage Access', false, error.message);
    return false;
  }
}

// Test 2: Test assessment route accessibility
async function testAssessmentRoutes() {
  log('Testing assessment routes...', 'test');

  const routes = [
    { path: '/assessment', name: 'Importance Form' },
    { path: '/second-step', name: 'Satisfaction Form' },
    { path: '/results', name: 'Results Page' },
    { path: '/dashboard', name: 'Dashboard' }
  ];

  let passedRoutes = 0;
  const routeDetails = [];

  for (const route of routes) {
    try {
      const response = execSync(`curl -s -o /dev/null -w "%{http_code}" "${FRONTEND_CONFIG.BASE_URL}${route.path}"`, {
        encoding: 'utf8',
        timeout: 5000
      });

      if (response.trim() === '200') {
        passedRoutes++;
        routeDetails.push(`${route.name}: OK`);
        log(`Route ${route.path} (${route.name}): OK`, 'info');
      } else {
        routeDetails.push(`${route.name}: HTTP ${response.trim()}`);
        log(`Route ${route.path} (${route.name}): HTTP ${response.trim()}`, 'warn');
      }
    } catch (error) {
      routeDetails.push(`${route.name}: Error`);
      log(`Route ${route.path} (${route.name}): Error - ${error.message}`, 'warn');
    }
  }

  if (passedRoutes === routes.length) {
    addTestResult('Assessment Routes', true, `All ${routes.length} routes accessible`);
    return true;
  } else {
    addTestResult('Assessment Routes', false, `Only ${passedRoutes}/${routes.length} routes accessible: ${routeDetails.join(', ')}`);
    return false;
  }
}

// Test 3: Check for React component errors by examining the HTML
async function testComponentRendering() {
  log('Testing component rendering...', 'component');

  try {
    // Test ImportanceForm rendering
    const importanceResponse = execSync(`curl -s "${FRONTEND_CONFIG.BASE_URL}/assessment"`, {
      encoding: 'utf8',
      timeout: 10000
    });

    // Test AssessmentForm rendering  
    const assessmentResponse = execSync(`curl -s "${FRONTEND_CONFIG.BASE_URL}/second-step"`, {
      encoding: 'utf8',
      timeout: 10000
    });

    // Test ResultsEnhanced rendering
    const resultsResponse = execSync(`curl -s "${FRONTEND_CONFIG.BASE_URL}/results"`, {
      encoding: 'utf8',
      timeout: 10000
    });

    // Basic checks for proper HTML structure (server-side rendering check)
    const responses = [
      { name: 'ImportanceForm', content: importanceResponse },
      { name: 'AssessmentForm', content: assessmentResponse },
      { name: 'ResultsEnhanced', content: resultsResponse }
    ];

    let validComponents = 0;
    const componentDetails = [];

    for (const response of responses) {
      if (response.content.includes('<div id="root">') && 
          response.content.includes('<!DOCTYPE html>') &&
          response.content.length > 500) { // Reasonable HTML size
        validComponents++;
        componentDetails.push(`${response.name}: OK`);
        log(`${response.name} renders properly`, 'component');
      } else {
        componentDetails.push(`${response.name}: Invalid HTML structure`);
        log(`${response.name} has rendering issues`, 'warn');
      }
    }

    if (validComponents === responses.length) {
      addTestResult('Component Rendering', true, 'All components render with proper HTML structure');
      return true;
    } else {
      addTestResult('Component Rendering', false, `${componentDetails.join(', ')}`);
      return false;
    }

  } catch (error) {
    addTestResult('Component Rendering', false, error.message);
    return false;
  }
}

// Test 4: Test static assets and CSS loading
async function testStaticAssets() {
  log('Testing static assets loading...', 'test');

  const assets = [
    '/src/main.tsx',
    '/src/App.tsx',
    '/src/index.css'
  ];

  let loadedAssets = 0;
  const assetDetails = [];

  for (const asset of assets) {
    try {
      const response = execSync(`curl -s -o /dev/null -w "%{http_code}" "${FRONTEND_CONFIG.BASE_URL}${asset}"`, {
        encoding: 'utf8',
        timeout: 5000
      });

      // Vite serves source files directly in dev mode
      if (response.trim() === '200') {
        loadedAssets++;
        assetDetails.push(`${asset}: OK`);
        log(`Asset ${asset}: OK`, 'info');
      } else {
        assetDetails.push(`${asset}: HTTP ${response.trim()}`);
        log(`Asset ${asset}: HTTP ${response.trim()}`, 'warn');
      }
    } catch (error) {
      assetDetails.push(`${asset}: Error`);
      log(`Asset ${asset}: Error - ${error.message}`, 'warn');
    }
  }

  // We expect at least main.tsx to be accessible in dev mode
  if (loadedAssets >= 1) {
    addTestResult('Static Assets', true, `${loadedAssets}/${assets.length} assets accessible`);
    return true;
  } else {
    addTestResult('Static Assets', false, `No assets accessible: ${assetDetails.join(', ')}`);
    return false;
  }
}

// Test 5: Test browser console for JavaScript errors (simulated)
async function testJavaScriptErrors() {
  log('Testing for JavaScript compilation errors...', 'test');

  try {
    // Check if we can build the project successfully (this will catch TS/JS errors)
    const buildResult = execSync('npm run build 2>&1', {
      encoding: 'utf8',
      timeout: 60000
    });

    if (buildResult.includes('built in') || buildResult.includes('Build completed')) {
      addTestResult('JavaScript Compilation', true, 'Project builds successfully without errors');
      return true;
    } else if (buildResult.includes('error') || buildResult.includes('Error')) {
      addTestResult('JavaScript Compilation', false, 'Build contains errors');
      return false;
    } else {
      addTestResult('JavaScript Compilation', true, 'Build completed (assuming success)');
      return true;
    }
  } catch (error) {
    // Build failed
    const errorOutput = error.stdout || error.stderr || error.message;
    if (errorOutput.includes('error') || errorOutput.includes('Error')) {
      addTestResult('JavaScript Compilation', false, 'TypeScript/JavaScript compilation errors detected');
      return false;
    } else {
      addTestResult('JavaScript Compilation', true, 'No obvious compilation errors');
      return true;
    }
  }
}

// Test 6: Simulate localStorage operations for assessment flow
async function testAssessmentDataFlow() {
  log('Testing assessment data flow simulation...', 'test');

  try {
    // Create a simple Node.js script to test localStorage simulation
    const testScript = `
// Mock browser environment for testing
const mockLocalStorage = {
  data: {},
  setItem(key, value) { 
    this.data[key] = value; 
    console.log('SET:', key, '=', typeof value === 'string' ? value.substring(0, 50) + '...' : value);
  },
  getItem(key) { 
    const value = this.data[key] || null;
    console.log('GET:', key, '=', value ? 'Found' : 'Not found');
    return value;
  },
  removeItem(key) { 
    delete this.data[key];
    console.log('REMOVE:', key);
  }
};

// Test importance data storage
const importanceData = ${JSON.stringify(FRONTEND_CONFIG.TEST_DATA.importance)};
const satisfactionData = ${JSON.stringify(FRONTEND_CONFIG.TEST_DATA.satisfaction)};

console.log('Testing importance data storage...');
mockLocalStorage.setItem('IMPORTANCE_RESULTS', JSON.stringify(importanceData));

console.log('Testing satisfaction data storage...');
const satisfactionResponses = {};
satisfactionData.forEach(item => {
  satisfactionResponses[item.category] = {
    [\`q_\${item.category}\`]: {
      questionId: \`q_\${item.category}\`,
      response: item.value
    }
  };
});
mockLocalStorage.setItem('SATISFACTION_RESPONSES', JSON.stringify(satisfactionResponses));

console.log('Testing results data storage...');
const mockResults = {
  overallScore: 42,
  categoryScores: {}
};
satisfactionData.forEach(item => {
  mockResults.categoryScores[item.category] = { score: item.value * 10 };
});
mockLocalStorage.setItem('JOB_SATISFACTION_RESULTS', JSON.stringify(mockResults));

console.log('Testing data retrieval...');
const retrievedImportance = mockLocalStorage.getItem('IMPORTANCE_RESULTS');
const retrievedSatisfaction = mockLocalStorage.getItem('SATISFACTION_RESPONSES');
const retrievedResults = mockLocalStorage.getItem('JOB_SATISFACTION_RESULTS');

if (retrievedImportance && retrievedSatisfaction && retrievedResults) {
  console.log('✅ All assessment data flow operations successful');
} else {
  console.log('❌ Assessment data flow failed');
}
`;

    const result = execSync(`node -e "${testScript}"`, {
      encoding: 'utf8',
      timeout: 10000
    });

    if (result.includes('✅ All assessment data flow operations successful')) {
      addTestResult('Assessment Data Flow', true, 'localStorage simulation successful');
      return true;
    } else {
      addTestResult('Assessment Data Flow', false, 'localStorage simulation failed');
      return false;
    }

  } catch (error) {
    addTestResult('Assessment Data Flow', false, error.message);
    return false;
  }
}

// Cleanup function
function cleanup() {
  log('Cleaning up frontend test resources...', 'info');

  if (frontendState.devServerProcess) {
    frontendState.devServerProcess.kill();
    log('Development server stopped', 'server');
  }
}

// Generate comprehensive report
function generateReport() {
  const passed = frontendState.testResults.filter(test => test.passed).length;
  const failed = frontendState.testResults.filter(test => !test.passed).length;
  const total = frontendState.testResults.length;
  const successRate = total > 0 ? ((passed / total) * 100).toFixed(1) : '0.0';

  log('\\n📊 FRONTEND TEST RESULTS', 'test');
  log(`Total Tests: ${total}`);
  log(`Passed: ${passed}`);
  log(`Failed: ${failed}`);
  log(`Success Rate: ${successRate}%`);

  if (failed > 0) {
    log('\\n❌ FAILED TESTS:', 'error');
    frontendState.testResults
      .filter(test => !test.passed)
      .forEach(test => {
        log(`  - ${test.name}: ${test.details}`, 'error');
      });
  }

  // Save detailed report
  const report = {
    timestamp: new Date().toISOString(),
    config: FRONTEND_CONFIG,
    summary: {
      total,
      passed,
      failed,
      successRate: parseFloat(successRate)
    },
    tests: frontendState.testResults,
    errors: frontendState.errors,
    serverStatus: frontendState.serverReady ? 'Ready' : 'Failed'
  };

  const reportPath = path.join(process.cwd(), 'frontend_test_report.json');
  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
  log(`📄 Frontend test report saved to: ${reportPath}`);

  // Provide recommendations
  log('\\n🔧 FRONTEND RECOMMENDATIONS:', 'test');
  if (successRate === '100.0') {
    log('✅ All frontend tests passed! Components are rendering and functioning correctly.');
    log('🔍 Ready for user acceptance testing.');
  } else if (successRate >= '80.0') {
    log('⚠️ Most frontend tests passed, minor issues detected.');
    log('🔍 Review failed tests and test manually in browser.');
  } else {
    log('❌ Multiple frontend test failures detected.');
    log('🔍 Components may have rendering or compilation issues.');
  }

  return successRate === '100.0';
}

// Main frontend test runner
async function runFrontendTests() {
  log('🚀 Starting Frontend Component Test Suite', 'test');
  log('This will test React components and user interface elements', 'info');

  // Set up cleanup on exit
  process.on('SIGINT', cleanup);
  process.on('SIGTERM', cleanup);
  process.on('exit', cleanup);

  try {
    // Ensure development server is running
    await ensureDevServer();
    await new Promise(resolve => setTimeout(resolve, 2000)); // Wait for server to stabilize

    log('\\n=== PHASE 1: BASIC CONNECTIVITY ===', 'test');
    await testHomepageAccess();
    await testAssessmentRoutes();

    log('\\n=== PHASE 2: COMPONENT VALIDATION ===', 'test');
    await testComponentRendering();
    await testStaticAssets();

    log('\\n=== PHASE 3: FUNCTIONALITY TESTS ===', 'test');
    await testJavaScriptErrors();
    await testAssessmentDataFlow();

    // Generate final report
    const allTestsPassed = generateReport();

    // Don't cleanup automatically - leave server running for manual testing
    if (frontendState.devServerProcess) {
      log('\\n🌐 Development server left running for manual testing at http://localhost:5173', 'server');
      log('Use Ctrl+C to stop the server when done', 'info');
    }

    return allTestsPassed;

  } catch (error) {
    log(`Fatal frontend test error: ${error.message}`, 'error');
    cleanup();
    return false;
  }
}

// Run the frontend tests
if (import.meta.url === \`file://\${process.argv[1]}\`) {
  runFrontendTests().then(success => {
    if (!success) {
      process.exit(1);
    }
    // Don't exit if successful - leave server running
  }).catch(error => {
    log(`Fatal error: ${error.message}`, 'error');
    cleanup();
    process.exit(1);
  });
}

export { runFrontendTests };

