    21→}
    22→
    23→export const checkStatus = async (): Promise<StatusResponse> => {
    24→  const response = await api.get('/api/status');
    25→  return response.data;
    26→};
    27→
    28→export const uploadFile = async (file: File): Promise<{message: string; filename: string}> => {
    29→  const formData = new FormData();
    30→  formData.append('file', file);
    31→  
    32→  const response = await api.post('/upload/', formData, {
    33→    headers: {
    34→      'Content-Type': 'multipart/form-data',
    35→    },
    36→  });
    37→  
    38→  return response.data;
    39→};
    40→
    41→export const fetchArtists = async (): Promise<ArtistListResponse> => {
    42→  const response = await api.get<ArtistListResponse>('/api/artists');
    43→  return response.data;
    44→};
    45→
    46→export const fetchKPIs = async (artist?: string): Promise<KPIResponse> => {
    47→  const params = artist ? { artist } : {};
    48→  const response = await api.get<KPIResponse>('/api/kpis', { params });
    49→  return response.data;
