import axios from 'axios';
import { KPIResponse, SalesOverTime, SalesByCityOverTime, SalesTrends, ArtistListResponse } from '@/types/api';

// Use relative URLs to leverage Vite's proxy configuration
const API_BASE_URL = '';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

export interface StatusResponse {
  ready: boolean;
  data_source: string;
  dataset_info: {
    csv_files_found: string[];
    missing_files: string[];
    artists_count: number;
  };
  error: string | null;
}

export const checkStatus = async (): Promise<StatusResponse> => {
  const response = await api.get('/api/status');
  return response.data;
};

export const uploadFile = async (file: File): Promise<{message: string; filename: string}> => {
  const formData = new FormData();
  formData.append('file', file);

  const response = await api.post('/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });

  return response.data;
};

export const fetchArtists = async (): Promise<ArtistListResponse> => {
  const response = await api.get<ArtistListResponse>('/api/artists');
  return response.data;
};

export const fetchKPIs = async (artist?: string): Promise<KPIResponse> => {
  const params = artist ? { artist } : {};
  const response = await api.get<KPIResponse>('/api/kpis', { params });
  return response.data;
};

export const fetchSalesOverTime = async (artist?: string): Promise<SalesOverTime[]> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesOverTime[]>('/api/sales-over-time', { params });
  return response.data;
};

export const fetchSalesTrends = async (artist?: string): Promise<SalesTrends> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesTrends>('/api/sales-trends', { params });
  return response.data;
};

export const fetchSalesByCityOverTime = async (artist?: string): Promise<SalesByCityOverTime[]> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesByCityOverTime[]>('/api/sales-by-city-over-time', { params });
  return response.data;
};

