    console.log(`📈 Available weeks: ${currentVerkaufsData.available_sales_weeks}`);

    // Load concerts
    const { concerts: concertsResponse } = await dataEntryApi.getConcerts();
    setConcerts(concertsResponse);

    // Fetch previous Verkaufswoche sales data
    const previousWeekSalesMap = new Map<string, number>();

    if (currentVerkaufsData.previous_week_available && currentVerkaufsData.previous_sales_week) {
      console.log(`🔍 Fetching previous Verkaufswoche ${currentVerkaufsData.previous_sales_week} data...`);

      try {
        const previousWeekData = await dataEntryApi.getSalesByVerkaufswoche(currentVerkaufsData.previous_sales_week);
        console.log(`✅ Previous Verkaufswoche data fetched: ${previousWeekData.sales.length} records`);

        // Create map of concert_id to previous week total sales
        previousWeekData.sales.forEach(sale => {
          const totalSold = typeof sale.tickets_sold_total === 'string' 
            ? parseInt(sale.tickets_sold_total) 
            : sale.tickets_sold_total;
          previousWeekSalesMap.set(sale.concert_id, totalSold);
          console.log(`💰 Concert ${sale.concert_id.substring(0, 8)}... had ${totalSold} tickets sold in VW ${currentVerkaufsData.previous_sales_week}`);
        });
      } catch (error) {
        console.log(`❌ Error fetching Verkaufswoche ${currentVerkaufsData.previous_sales_week} data:`, error);
      }
    } else {
      console.log(`ℹ️ No previous Verkaufswoche data available`);
    }

    // Initialize sales entries with previous week data
    const initialEntries = concertsResponse.map(concert => ({
      concert_id: concert.concert_id,
      concert_date: concert.concert_date,
      venue_name: concert.venue_name || '',
      city: concert.city || '',
      artist_name: concert.artist_name || '',
      tour_name: concert.tour_name || '',
      capacity: concert.capacity || 0,
      tickets_sold_in_week: 0,
      tickets_sold_total: 0,
      previous_week_sales: previousWeekSalesMap.get(concert.concert_id) || 0
    }));

    console.log(`🎯 Final mapping: ${previousWeekSalesMap.size} concerts with previous Verkaufswoche data`);
    console.log(`🎪 Total concerts: ${initialEntries.length}`);
    const concertsWithPrevSales = initialEntries.filter(e => e.previous_week_sales > 0);
    console.log(`✨ Concerts with previous week sales: ${concertsWithPrevSales.length}`);
    if (concertsWithPrevSales.length > 0) {
      console.log(`📈 Examples:`, concertsWithPrevSales.slice(0, 3).map(c => 
        `${c.artist_name} at ${c.venue_name}: ${c.previous_week_sales} tickets`
      ));
      // Alert to confirm the fix is working
      alert(`🎉 SUCCESS! Found ${concertsWithPrevSales.length} concerts with previous Verkaufswoche sales data! The fix is working!`);
    }

    setSalesEntries(initialEntries);
  } catch (err) {
    setError('Failed to load data');
    console.error('Error loading data:', err);
  } finally {
    setLoading(false);
  }
};

const calculatePreviousWeekSales = async () => {
  try {
    // Get current Verkaufswoche info
    const currentVerkaufsData = await dataEntryApi.getCurrentVerkaufswoche();
    const previousWeekSalesMap = new Map<string, number>();

    if (currentVerkaufsData.previous_week_available && currentVerkaufsData.previous_sales_week) {
      console.log(`🔍 Calculating previous Verkaufswoche ${currentVerkaufsData.previous_sales_week} sales...`);

      try {
        const previousWeekData = await dataEntryApi.getSalesByVerkaufswoche(currentVerkaufsData.previous_sales_week);

        // Create map of concert_id to previous week total sales
        previousWeekData.sales.forEach(sale => {
          const totalSold = typeof sale.tickets_sold_total === 'string' 
            ? parseInt(sale.tickets_sold_total) 
            : sale.tickets_sold_total;
          previousWeekSalesMap.set(sale.concert_id, totalSold);
        });
      } catch (error) {
        console.log(`❌ Error fetching Verkaufswoche ${currentVerkaufsData.previous_sales_week} data:`, error);
      }
    } else {
      console.log(`ℹ️ No previous Verkaufswoche data available for calculation`);
    }

    return previousWeekSalesMap;
  } catch (error) {
    console.error('Error calculating previous Verkaufswoche sales:', error);
    return new Map<string, number>();
  }
};

const handleArtistFilter = async (artistName: string) => {
  setSelectedArtist(artistName);
  // Clear search when an artist is selected
  setArtistSearch('');

  const previousWeekSalesMap = await calculatePreviousWeekSales();

  if (artistName === '') {
    // Show all concerts
    const allEntries = concerts.map(concert => ({
      concert_id: concert.concert_id,
