from fastapi import FastAPI, HTTPException, Query
from fastapi.middleware.cors import CORSMiddleware
from data_processor_v2 import DataProcessorV2
from csv_manager import CSVManager
from pydantic import BaseModel, Field
from typing import Optional, List, Dict, Any
import io
import os
from pathlib import Path
from datetime import datetime

app = FastAPI()

# Configure CORS
app.add_middleware(
    CORSMiddleware,
    allow_origins=["http://localhost:3000", "http://localhost:3001", "http://localhost:3002", "http://localhost:3003"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# Global data processor and CSV manager
data_processor = None
csv_manager = CSVManager()
original_csv_files = None
dataset_status = {
    "data_loaded": False,
    "data_source": "csv_files",
    "csv_files_found": [],
    "missing_files": [],
    "load_error": None,
    "artists_count": 0
}

# Pydantic models for request validation
class ArtistCreate(BaseModel):
    artist_name: str = Field(..., min_length=1, max_length=100)

class VenueCreate(BaseModel):
    venue_name: str = Field(..., min_length=1, max_length=100)
    city: str = Field(..., min_length=1, max_length=100)
    capacity: int = Field(..., ge=1, le=100000)

class TourCreate(BaseModel):
    tour_name: str = Field(..., min_length=1, max_length=100)
    artist_id: str = Field(..., min_length=1)

class ConcertCreate(BaseModel):
    concert_date: str = Field(..., pattern=r"\d{2}\.\d{2}\.\d{4}")  # DD.MM.YYYY format
    tour_id: str = Field(..., min_length=1)
    venue_id: str = Field(..., min_length=1)

class WeeklySalesCreate(BaseModel):
    concert_id: str = Field(..., min_length=1)
    calendar_week: str = Field(..., pattern=r"KW \d{1,2}")  # KW XX format
    sales_week_number: int = Field(..., ge=1, le=52)
    tickets_sold_in_week: int = Field(..., ge=0)
    tickets_sold_total: int = Field(..., ge=0)

class WeeklySalesUpdate(BaseModel):
    tickets_sold_in_week: int = Field(..., ge=0)
    tickets_sold_total: int = Field(..., ge=0)

class BulkWeeklySalesEntry(BaseModel):
    entries: List[Dict[str, Any]] = Field(..., min_items=1)

def check_csv_dataset():
    """Check if complete CSV dataset exists"""
    global dataset_status

    # Define the folder path (relative to backend directory)
    folder_path = Path("../../VVK_Zahlen")
    required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

    dataset_status["csv_files_found"] = []
    dataset_status["missing_files"] = []

    for filename in required_files:
        file_path = folder_path / f"{filename}.csv"
        if file_path.exists():
            dataset_status["csv_files_found"].append(f"{filename}.csv")
        else:
            dataset_status["missing_files"].append(f"{filename}.csv")

    # Dataset is complete if all required files are found
    return len(dataset_status["missing_files"]) == 0

def load_csv_data():
    """Load CSV data on startup"""
    global data_processor, original_csv_files, dataset_status

    try:
        print("🔍 Checking for CSV dataset...")

        if not check_csv_dataset():
            missing_files_str = ", ".join(dataset_status["missing_files"])
            error_msg = f"Incomplete dataset. Missing files: {missing_files_str}"
            print(f"❌ {error_msg}")
            dataset_status["load_error"] = error_msg
            return False

        print("✅ Complete CSV dataset found!")
        print("🚀 Loading CSV data...")

        # Define the folder path (relative to backend directory)
        folder_path = "../../VVK_Zahlen"

        # Load CSV files from folder
        csv_files = {}
        required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

        for filename in required_files:
            file_path = os.path.join(folder_path, f"{filename}.csv")

            with open(file_path, 'rb') as f:
                csv_files[filename] = io.BytesIO(f.read())
            print(f"✅ Loaded {filename}.csv")

        # Store original files for artist filtering
        original_csv_files = csv_files

        # Create data processor without artist filter
        data_processor = DataProcessorV2(csv_files)

        artists_count = len(data_processor.get_available_artists())
        dataset_status["data_loaded"] = True
        dataset_status["artists_count"] = artists_count
        dataset_status["load_error"] = None

        print(f"🎉 Successfully loaded {artists_count} artists from CSV data!")
        print("📊 Dashboard ready to serve data!")

        return True

    except Exception as e:
        error_msg = f"Error loading CSV data: {str(e)}"
        print(f"❌ {error_msg}")
        dataset_status["load_error"] = error_msg
        dataset_status["data_loaded"] = False
        import traceback
        traceback.print_exc()
        return False

# Load data on startup
print("🏁 Starting Sales Dashboard API v2.0...")
if not load_csv_data():
    print("⚠️  Failed to load CSV data on startup. Dashboard will run in limited mode.")
    print("💡 Place complete CSV dataset in VVK_Zahlen/ directory and restart the server.")

@app.get("/")
def read_root():
    return {
        "message": "Sales Dashboard API v2.0 - CSV Database Mode",
        "version": "2.0.0",
        "data_source": dataset_status["data_source"],
        "dataset_status": dataset_status,
        "ready": dataset_status["data_loaded"],
        "endpoints": {
            "status": "GET /api/status",
            "artists": "GET /api/artists",
            "venues": "GET /api/venues",
            "tours": "GET /api/tours",
            "concerts": "GET /api/concerts",
            "sales_current_week": "GET /api/sales/current-week",
            "sales_by_week": "GET /api/sales/week/{week_number}",
            "sales_current_verkaufswoche": "GET /api/sales/current-verkaufswoche",
            "sales_by_verkaufswoche": "GET /api/sales/verkaufswoche/{sales_week}",
            "create_artist": "POST /api/artists",
            "create_venue": "POST /api/venues", 
            "create_tour": "POST /api/tours",
            "create_concert": "POST /api/concerts",
            "create_weekly_sales": "POST /api/sales/weekly",
            "update_weekly_sales": "PUT /api/sales/{sale_id}",
            "bulk_weekly_sales": "POST /api/sales/bulk",
            "kpis": "GET /api/kpis?artist=<artist_name>",
            "sales_over_time": "GET /api/sales-over-time?artist=<artist_name>",
            "sales_trends": "GET /api/sales-trends?artist=<artist_name>",
            "sales_by_city_over_time": "GET /api/sales-by-city-over-time?artist=<artist_name>",
            "reload_data": "POST /reload-data",
            "docs": "GET /docs"
        }
    }

@app.get("/api/status")
def get_status():
    """Get detailed status about data availability"""
    return {
        "ready": dataset_status["data_loaded"],
        "data_source": dataset_status["data_source"],
        "dataset_info": {
            "csv_files_found": dataset_status["csv_files_found"],
            "missing_files": dataset_status["missing_files"],
            "artists_count": dataset_status["artists_count"]
        },
        "error": dataset_status["load_error"]
    }

@app.post("/reload-data/")
async def reload_data():
    """Reload CSV data"""
    if load_csv_data():
        return {
            "message": "CSV data reloaded successfully",
            "artists_loaded": len(data_processor.get_available_artists()) if data_processor else 0
        }
    else:
        raise HTTPException(status_code=500, detail="Failed to reload CSV data")

# Data Input API Endpoints

@app.get("/api/venues")
def get_venues():
    """Get all venues"""
    try:
        venues = csv_manager.read_csv('venues')
        return {"venues": venues}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading venues: {str(e)}")

@app.get("/api/tours")
def get_tours():
    """Get all tours"""
    try:
        tours = csv_manager.read_csv('tours')
        return {"tours": tours}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading tours: {str(e)}")

@app.get("/api/concerts")
def get_concerts(artist_id: Optional[str] = Query(None), tour_id: Optional[str] = Query(None)):
    """Get concerts with optional filtering"""
    try:
        concerts = csv_manager.get_concerts_for_current_week()

        # Apply filters
        if artist_id:
            concerts = [c for c in concerts if c.get('artist_id') == artist_id]
        if tour_id:
            concerts = [c for c in concerts if c.get('tour_id') == tour_id]

        return {"concerts": concerts}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading concerts: {str(e)}")

@app.get("/api/sales/current-week")
def get_current_week_sales():
    """Get current week sales data"""
    try:
        current_week = csv_manager.get_current_week()
        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for current week
        current_week_sales = [
            sale for sale in weekly_sales 
            if sale.get('calendar_week') == f"KW {current_week}"
        ]

        return {
            "current_week": current_week,
            "calendar_week": f"KW {current_week}",
            "sales": current_week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading current week sales: {str(e)}")

@app.get("/api/sales/week/{week_number}")
def get_sales_by_week(week_number: int):
    """Get sales data for a specific week"""
    try:
        if week_number < 1 or week_number > 53:
            raise HTTPException(status_code=400, detail="Week number must be between 1 and 53")

        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for specified week
        week_sales = [
            sale for sale in weekly_sales 
            if sale.get('calendar_week') == f"KW {week_number}"
        ]

        return {
            "week": week_number,
            "calendar_week": f"KW {week_number}",
            "sales": week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading week {week_number} sales: {str(e)}")

@app.get("/api/sales/verkaufswoche/{sales_week}")
def get_sales_by_verkaufswoche(sales_week: int):
    """Get sales data for a specific Verkaufswoche (sales week number)"""
    try:
        if sales_week < 1:
            raise HTTPException(status_code=400, detail="Sales week number must be positive")

        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for specified Verkaufswoche
        week_sales = [
            sale for sale in weekly_sales 
            if int(sale.get('sales_week_number', 0)) == sales_week
        ]

        return {
            "sales_week": sales_week,
            "sales": week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading Verkaufswoche {sales_week} sales: {str(e)}")

@app.get("/api/sales/current-verkaufswoche")
def get_current_verkaufswoche_sales():
    """Get current Verkaufswoche sales data with previous week info"""
    try:
        weekly_sales = csv_manager.read_csv('weekly_sales')

        if not weekly_sales:
            return {
                "current_sales_week": 1,
                "previous_sales_week": None,
                "sales": [],
                "previous_week_available": False
            }

        # Find the highest sales_week_number (current)
        current_sales_week = max(int(sale.get('sales_week_number', 1)) for sale in weekly_sales)

        # Get all available sales weeks
        available_weeks = sorted(set(int(sale.get('sales_week_number', 1)) for sale in weekly_sales))

        # Find the most recent previous week with data
        previous_sales_week = None
        for week in reversed(available_weeks):
            if week < current_sales_week:
                previous_sales_week = week
                break

        # Get current week sales
        current_week_sales = [
            sale for sale in weekly_sales 
            if int(sale.get('sales_week_number', 0)) == current_sales_week
        ]

        return {
            "current_sales_week": current_sales_week,
            "previous_sales_week": previous_sales_week,
            "available_sales_weeks": available_weeks,
            "sales": current_week_sales,
            "previous_week_available": previous_sales_week is not None
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading current Verkaufswoche sales: {str(e)}")

@app.post("/api/artists")
def create_artist(artist: ArtistCreate):
    """Create a new artist"""
    try:
        # Check if artist already exists
        existing_artists = csv_manager.read_csv('artists')
        for existing in existing_artists:
            if existing['artist_name'].lower() == artist.artist_name.lower():
                raise HTTPException(status_code=400, detail="Artist already exists")

        artist_id = csv_manager.add_record('artists', {
            'artist_name': artist.artist_name
        })

        return {
            "message": "Artist created successfully",
            "artist_id": artist_id,
            "artist_name": artist.artist_name
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating artist: {str(e)}")

@app.post("/api/venues")
def create_venue(venue: VenueCreate):
    """Create a new venue"""
    try:
        venue_id = csv_manager.add_record('venues', {
            'venue_name': venue.venue_name,
            'city': venue.city,
            'capacity': str(venue.capacity)
        })

        return {
            "message": "Venue created successfully",
            "venue_id": venue_id,
            "venue_name": venue.venue_name,
            "city": venue.city,
            "capacity": venue.capacity
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating venue: {str(e)}")

@app.post("/api/tours")
def create_tour(tour: TourCreate):
    """Create a new tour"""
    try:
        # Validate artist exists
        artists = csv_manager.read_csv('artists')
        if not any(a['artist_id'] == tour.artist_id for a in artists):
            raise HTTPException(status_code=400, detail="Artist not found")

        tour_id = csv_manager.add_record('tours', {
            'tour_name': tour.tour_name,
            'artist_id': tour.artist_id
        })

        return {
            "message": "Tour created successfully",
            "tour_id": tour_id,
            "tour_name": tour.tour_name,
            "artist_id": tour.artist_id
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating tour: {str(e)}")

@app.post("/api/concerts")
def create_concert(concert: ConcertCreate):
    """Create a new concert"""
    try:
        # Validate tour exists
        tours = csv_manager.read_csv('tours')
        if not any(t['tour_id'] == concert.tour_id for t in tours):
            raise HTTPException(status_code=400, detail="Tour not found")

        # Validate venue exists
        venues = csv_manager.read_csv('venues')
        if not any(v['venue_id'] == concert.venue_id for v in venues):
            raise HTTPException(status_code=400, detail="Venue not found")

        # Validate date format
        try:
            datetime.strptime(concert.concert_date, "%d.%m.%Y")
        except ValueError:
            raise HTTPException(status_code=400, detail="Invalid date format. Use DD.MM.YYYY")

        concert_id = csv_manager.add_record('concerts', {
            'concert_date': concert.concert_date,
            'tour_id': concert.tour_id,
            'venue_id': concert.venue_id
        })

        return {
            "message": "Concert created successfully",
            "concert_id": concert_id,
            "concert_date": concert.concert_date,
            "tour_id": concert.tour_id,
            "venue_id": concert.venue_id
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating concert: {str(e)}")

@app.post("/api/sales/weekly")
def create_weekly_sales(sales: WeeklySalesCreate):
    """Create weekly sales entry"""
    try:
        # Validate concert exists
        concerts = csv_manager.read_csv('concerts')
        concert = None
        for c in concerts:
            if c['concert_id'] == sales.concert_id:
                concert = c
                break

        if not concert:
            raise HTTPException(status_code=400, detail="Concert not found")

        # Validate venue capacity
        if not csv_manager.validate_venue_capacity(concert['venue_id'], sales.tickets_sold_total):
            raise HTTPException(status_code=400, detail="Tickets sold exceeds venue capacity")

        # Validate tickets_sold_total >= tickets_sold_in_week
        if sales.tickets_sold_total < sales.tickets_sold_in_week:
            raise HTTPException(status_code=400, detail="Total tickets sold cannot be less than weekly sales")

        sale_id = csv_manager.add_record('weekly_sales', {
            'concert_id': sales.concert_id,
            'calendar_week': sales.calendar_week,
            'sales_week_number': str(sales.sales_week_number),
            'tickets_sold_in_week': str(sales.tickets_sold_in_week),
            'tickets_sold_total': str(sales.tickets_sold_total)
        })

        return {
            "message": "Weekly sales entry created successfully",
            "sale_id": sale_id,
            "concert_id": sales.concert_id,
            "calendar_week": sales.calendar_week,
            "tickets_sold_in_week": sales.tickets_sold_in_week,
            "tickets_sold_total": sales.tickets_sold_total
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating weekly sales: {str(e)}")

@app.put("/api/sales/{sale_id}")
def update_weekly_sales(sale_id: str, sales: WeeklySalesUpdate):
    """Update existing weekly sales entry"""
    try:
        # Validate sale exists
        weekly_sales = csv_manager.read_csv('weekly_sales')
        sale = None
        for s in weekly_sales:
            if s['sale_id'] == sale_id:
                sale = s
                break

        if not sale:
            raise HTTPException(status_code=404, detail="Sales entry not found")

        # Get concert and validate capacity
        concerts = csv_manager.read_csv('concerts')
        concert = None
        for c in concerts:
            if c['concert_id'] == sale['concert_id']:
                concert = c
                break

        if concert and not csv_manager.validate_venue_capacity(concert['venue_id'], sales.tickets_sold_total):
            raise HTTPException(status_code=400, detail="Tickets sold exceeds venue capacity")

        # Validate tickets_sold_total >= tickets_sold_in_week
        if sales.tickets_sold_total < sales.tickets_sold_in_week:
            raise HTTPException(status_code=400, detail="Total tickets sold cannot be less than weekly sales")

        success = csv_manager.update_record('weekly_sales', sale_id, {
            'tickets_sold_in_week': str(sales.tickets_sold_in_week),
            'tickets_sold_total': str(sales.tickets_sold_total)
        })

        if not success:
            raise HTTPException(status_code=404, detail="Sales entry not found")

        return {
            "message": "Weekly sales entry updated successfully",
            "sale_id": sale_id,
            "tickets_sold_in_week": sales.tickets_sold_in_week,
            "tickets_sold_total": sales.tickets_sold_total
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating weekly sales: {str(e)}")

@app.post("/api/sales/bulk")
def bulk_weekly_sales(bulk_sales: BulkWeeklySalesEntry):
    """Bulk create/update weekly sales entries"""
    try:
        results = []
        errors = []

        for i, entry in enumerate(bulk_sales.entries):
            try:
                # Validate entry format
                required_fields = ['concert_id', 'calendar_week', 'sales_week_number', 'tickets_sold_in_week', 'tickets_sold_total']
                for field in required_fields:
                    if field not in entry:
                        errors.append(f"Entry {i+1}: Missing required field '{field}'")
                        continue

                # Create or update entry
                sale_id = csv_manager.add_record('weekly_sales', {
                    'concert_id': entry['concert_id'],
                    'calendar_week': entry['calendar_week'],
                    'sales_week_number': str(entry['sales_week_number']),
                    'tickets_sold_in_week': str(entry['tickets_sold_in_week']),
                    'tickets_sold_total': str(entry['tickets_sold_total'])
                })

                results.append({
                    "entry_index": i + 1,
                    "sale_id": sale_id,
                    "status": "success"
                })

            except Exception as e:
                errors.append(f"Entry {i+1}: {str(e)}")

        return {
            "message": f"Bulk operation completed. {len(results)} successful, {len(errors)} errors",
            "successful_entries": len(results),
            "errors": errors,
            "results": results
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error in bulk operation: {str(e)}")

def get_filtered_processor(artist: str):
    """Helper function to create a filtered processor for a specific artist."""
    if not original_csv_files:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    # Create new BytesIO objects from the original data
    csv_files_copy = {}
    for name, original_stream in original_csv_files.items():
        original_stream.seek(0)  # Reset to beginning
        csv_files_copy[name] = io.BytesIO(original_stream.read())

    return DataProcessorV2(csv_files_copy, artist_filter=artist)

