const calculatePreviousWeekSales = async () => {
  try {
    // Get current Verkaufswoche info
    const currentVerkaufsData = await dataEntryApi.getCurrentVerkaufswoche();
    const previousWeekSalesMap = new Map<string, number>();

    if (currentVerkaufsData.previous_week_available && currentVerkaufsData.previous_sales_week) {
