@app.post("/api/concerts")
def create_concert(concert: ConcertCreate):
    """Create a new concert"""
    try:
        # Validate tour exists
        tours = csv_manager.read_csv('tours')
        if not any(t['tour_id'] == concert.tour_id for t in tours):
            raise HTTPException(status_code=400, detail="Tour not found")

        # Validate venue exists
        venues = csv_manager.read_csv('venues')
        if not any(v['venue_id'] == concert.venue_id for v in venues):
            raise HTTPException(status_code=400, detail="Venue not found")

        # Validate date format
        try:
            datetime.strptime(concert.concert_date, "%d.%m.%Y")
        except ValueError:
            raise HTTPException(status_code=400, detail="Invalid date format. Use DD.MM.YYYY")

        concert_id = csv_manager.add_record('concerts', {
            'concert_date': concert.concert_date,
            'tour_id': concert.tour_id,
            'venue_id': concert.venue_id
        })

        return {
            "message": "Concert created successfully",
            "concert_id": concert_id,
            "concert_date": concert.concert_date,
            "tour_id": concert.tour_id,
            "venue_id": concert.venue_id
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating concert: {str(e)}")

@app.post("/api/sales/weekly")
def create_weekly_sales(sales: WeeklySalesCreate):
    """Create weekly sales entry"""
    try:
        # Validate concert exists
        concerts = csv_manager.read_csv('concerts')
        concert = None
        for c in concerts:
            if c['concert_id'] == sales.concert_id:
                concert = c
                break

        if not concert:
            raise HTTPException(status_code=400, detail="Concert not found")

        # Validate venue capacity
        if not csv_manager.validate_venue_capacity(concert['venue_id'], sales.tickets_sold_total):
            raise HTTPException(status_code=400, detail="Tickets sold exceeds venue capacity")

        # Validate tickets_sold_total >= tickets_sold_in_week
        if sales.tickets_sold_total < sales.tickets_sold_in_week:
            raise HTTPException(status_code=400, detail="Total tickets sold cannot be less than weekly sales")

        sale_id = csv_manager.add_record('weekly_sales', {
            'concert_id': sales.concert_id,
            'calendar_week': sales.calendar_week,
            'sales_week_number': str(sales.sales_week_number),
            'tickets_sold_in_week': str(sales.tickets_sold_in_week),
            'tickets_sold_total': str(sales.tickets_sold_total)
        })

        return {
            "message": "Weekly sales entry created successfully",
            "sale_id": sale_id,
            "concert_id": sales.concert_id,
            "calendar_week": sales.calendar_week,
            "tickets_sold_in_week": sales.tickets_sold_in_week,
            "tickets_sold_total": sales.tickets_sold_total
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating weekly sales: {str(e)}")

@app.put("/api/sales/{sale_id}")
def update_weekly_sales(sale_id: str, sales: WeeklySalesUpdate):
    """Update existing weekly sales entry"""
    try:
        # Validate sale exists
        weekly_sales = csv_manager.read_csv('weekly_sales')
        sale = None
        for s in weekly_sales:
            if s['sale_id'] == sale_id:
                sale = s
                break

        if not sale:
            raise HTTPException(status_code=404, detail="Sales entry not found")

        # Get concert and validate capacity
        concerts = csv_manager.read_csv('concerts')
        concert = None
        for c in concerts:
            if c['concert_id'] == sale['concert_id']:
                concert = c
                break

        if concert and not csv_manager.validate_venue_capacity(concert['venue_id'], sales.tickets_sold_total):
            raise HTTPException(status_code=400, detail="Tickets sold exceeds venue capacity")

        # Validate tickets_sold_total >= tickets_sold_in_week
        if sales.tickets_sold_total < sales.tickets_sold_in_week:
            raise HTTPException(status_code=400, detail="Total tickets sold cannot be less than weekly sales")

        success = csv_manager.update_record('weekly_sales', sale_id, {
            'tickets_sold_in_week': str(sales.tickets_sold_in_week),
            'tickets_sold_total': str(sales.tickets_sold_total)
        })

        if not success:
            raise HTTPException(status_code=404, detail="Sales entry not found")

        return {
            "message": "Weekly sales entry updated successfully",
            "sale_id": sale_id,
            "tickets_sold_in_week": sales.tickets_sold_in_week,
            "tickets_sold_total": sales.tickets_sold_total
        }
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating weekly sales: {str(e)}")

@app.post("/api/sales/bulk")
def bulk_weekly_sales(bulk_sales: BulkWeeklySalesEntry):
    """Bulk create/update weekly sales entries"""
    try:
        results = []
        errors = []

        for i, entry in enumerate(bulk_sales.entries):
            try:
                # Validate entry format
                required_fields = ['concert_id', 'calendar_week', 'sales_week_number', 'tickets_sold_in_week', 'tickets_sold_total']
                for field in required_fields:
                    if field not in entry:
                        errors.append(f"Entry {i+1}: Missing required field '{field}'")
                        continue

                # Create or update entry
                sale_id = csv_manager.add_record('weekly_sales', {
                    'concert_id': entry['concert_id'],
                    'calendar_week': entry['calendar_week'],
                    'sales_week_number': str(entry['sales_week_number']),
                    'tickets_sold_in_week': str(entry['tickets_sold_in_week']),
                    'tickets_sold_total': str(entry['tickets_sold_total'])
                })

                results.append({
                    "entry_index": i + 1,
                    "sale_id": sale_id,
                    "status": "success"
                })

            except Exception as e:
                errors.append(f"Entry {i+1}: {str(e)}")

        return {
            "message": f"Bulk operation completed. {len(results)} successful, {len(errors)} errors",
            "successful_entries": len(results),
            "errors": errors,
            "results": results
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error in bulk operation: {str(e)}")

def get_filtered_processor(artist: str):
    """Helper function to create a filtered processor for a specific artist."""
    if not original_csv_files:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    # Create new BytesIO objects from the original data
    csv_files_copy = {}
    for name, original_stream in original_csv_files.items():
        original_stream.seek(0)  # Reset to beginning
        csv_files_copy[name] = io.BytesIO(original_stream.read())

    return DataProcessorV2(csv_files_copy, artist_filter=artist)

